INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (3401, 'Lidocaine', 'Lemborexant', 'Moderate', 'Coadministration with lemborexant may decrease the plasma concentrations and pharmacologic effects of CYP450 2B6 substrates. The proposed mechanism is lemborexant-mediated induction of the CYP450 2B6 isoenzyme.', NULL, 'Caution is advised if lemborexant is used concomitantly with drugs that are substrates of CYP450 2B6.', NULL, 'Caution is advised if lemborexant is used concomitantly with drugs that are substrates of CYP450 2B6. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lemborexant is added to or withdrawn from therapy.', 'Metabolism', 'Procaine, Tetracaine, Tetracaine, Bacitracin, Flurbiprofen, Ibuprofen, Chlorhexidine, Povidone-iodine, Neomycin, Quinidine, Propafenone, More', 'Suvorexant, Chloral hydrate, Thiopental, Paraldehyde, Propiomazine, Ramelteon, Eszopiclone, Methohexital, Ethchlorvynol, Tasimelteon, Melatonin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/3401/', '[1] "Product Information. Dayvigo (lemborexant)." Eisai Inc, Woodcliff Lake, NJ.[2] "Product Information. Dayvigo (lemborexant)." Eisai Inc, Woodcliff Lake, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (3402, 'Lindane', 'Lidocaine', 'Moderate', 'Lindane penetrates human skin and has the potential to cause central nervous system toxicity. Seizures have been reported after excessive use or oral ingestion of lindane. There may be a theoretical risk of increased seizure potential when lindane is used with selective serotonin reuptake inhibitors (SSRI antidepressants or anorectics), monoamine oxidase inhibitors, neuroleptic agents, central nervous system stimulants, opioids, tricyclic antidepressants, other tricyclic compounds (e.g., cyclobenzaprine, phenothiazines), and/or any substance that can reduce the seizure threshold (e.g., carbapenems, cholinergic agents, fluoroquinolones, interferons, chloroquine, mefloquine, theophylline).', NULL, 'Caution is advised if lindane is used with any substance that can reduce the seizure threshold, particularly in the very young or the elderly and in patients with epilepsy, a history of seizures, or other risk factors for seizures (e.', NULL, 'Caution is advised if lindane is used with any substance that can reduce the seizure threshold, particularly in the very young or the elderly and in patients with epilepsy, a history of seizures, or other risk factors for seizures (e.g., head trauma, brain tumor, metabolic disorders, alcohol and drug withdrawal, CNS infections). Lindane should be used according to recommended dosage and directions for application.', 'Synergism', 'Procaine, Tetracaine, Clemastine, Tetracaine, Mepyramine, Diphenhydramine, Tripelennamine, Pheniramine, Bacitracin, Flurbiprofen, Ibuprofen, More', 'Disulfiram', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/3402/', '[1] Cox R, Krupnick J, Bush N, Houpt A "Seizures caused by concomitant use of lindane and dextroamphetamine in a child with attention deficit hyperactivity disorder." J Miss State Med Assoc 41 (2000): 690-2[2] Matsuoka LY "Convulsions following application of gamma benzene hexachloride." J Am Acad Dermatol 5 (1981): 98-9[3] "Product Information. Kwell (lindane)." Reed and Carnrick, Jersey City, NJ.[4] Telch J, Jarvis DA "Acute intoxication with lindane (gamma benzene hexachloride)." Can Med Assoc J 126 (1982): 662-3[5] Pramanik AK, Hansen RC "Transcutaneous gamma benzene hexachloride absorption and toxicity in infants and children." Arch Dermatol 115 (1979): 1224-5[6] Ramchander V, Cameron ES, Reid HF "Lindane toxicity in an infant." West Indian Med J 40 (1991): 41-3[7] Tenenbein M "Seizures after lindane therapy." J Am Geriatr Soc 39 (1991): 394-5[8] Solomon BA, Haut SR, Carr EM, Shalita AR "Neurotoxic reaction to lindane in an HIV-seropositive patient: an old medication''s new problem." J Fam Pract 40 (1995): 291-6[9] Munk ZM, Nantel A "Acute lindane poisoning with development of muscle necrosis." Can Med Assoc J 117 (1977): 1050-4', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (3403, 'Lonafarnib', 'Lidocaine', 'Moderate', 'Coadministration with lonafarnib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. Lonafarnib is a potent CYP450 3A time-dependent and mechanism-based inhibitor in vitro.', NULL, 'Caution is advised when lonafarnib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when lonafarnib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lonafarnib is added to or withdrawn from therapy. Patients should be monitored for the development of adverse effects. The prescribing information recommends avoiding concomitant use of lonafarnib and sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges when possible. These drugs include, but are not limited to: alfentanil, cisapride, colchicine, cyclosporine, fentanyl, ivacaftor, lovastatin, simvastatin, oral midazolam, triazolam, pimozide, quinidine, sildenafil, ergot derivatives, macrolide immunosuppressants, and vinca alkaloids.', 'Metabolism', 'Procaine, Tetracaine, Clemastine, Tetracaine, Mepyramine, Diphenhydramine, Tripelennamine, Doxepin, Pheniramine, Promethazine, Bacitracin, More', 'Teduglutide, Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Glycerol phenylbutyrate, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, Phenylbutyric acid, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/3403/', '[1] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.[2] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.', 1767369485);
