INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (3476, 'Nitrous acid', 'Lidocaine', 'Major', 'Sodium nitrite can cause methemoglobin formation, which diminishes oxygen-carrying capacity of the blood. Coadministration with other agents that are also associated with methemoglobinemia including local anesthetics (e.g., benzocaine, lidocaine, prilocaine), antimalarials (e.g., chloroquine, primaquine, quinine, tafenoquine), nitrates and nitrites, sulfonamides, acetaminophen, aminosalicylic acid, dapsone, dimethyl sulfoxide, flutamide, metoclopramide (primarily in infants), nitrofurantoin (primarily in infants), phenazopyridine, phenobarbital, phenytoin, and rasburicase may increase the risk.', NULL, 'Sodium nitrite should be used with caution in the presence of other methemoglobin-inducing drugs.', NULL, 'Sodium nitrite should be used with caution in the presence of other methemoglobin-inducing drugs. Patients should be closely monitored to ensure adequate perfusion and oxygenation during treatment with sodium nitrite. Methemoglobin levels should be monitored and oxygen administered whenever possible.', 'Synergism', 'Clemastine, Mepyramine, Diphenhydramine, Tripelennamine, Doxepin, Pheniramine, Promethazine, Bacitracin, Flurbiprofen, Ibuprofen, Chlorhexidine, More', 'Hydroxocobalamin, Fomepizole, Ipecac, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Deferoxamine, Levoleucovorin, Arginine, Methionine, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/3476/', '[1] "Product Information. Sodium Nitrite (sodium nitrite)." Hope Pharmaceuticals, Scottsdale, AZ.[2] Rehman HU "Methemoglobinemia." West J Med 175 (2001): 193-6[3] Coleman MD, Coleman NA "Drug-indu