INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1325877, 'Fentanyl', 'Aminoglutethimide', 'Major', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of opioids that are primarily metabolized by the isoenzyme such as butorphanol, fentanyl, hydrocodone, and oxycodone. Reduced efficacy or withdrawal symptoms may occur in patients maintained on their narcotic pain regimen following the addition of a CYP450 3A4 inducer. Conversely, discontinuation of the inducer may increase opioid plasma concentrations and potentiate the risk of overdose and fatal respiratory depression.', 'DDInter', 1767317474, 'Pharmacologic response to the opioid should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the opioid dosage adjusted as necessary.', 'Metabolism', 'Pharmacologic response to the opioid should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the opioid dosage adjusted as necessary.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1325878, 'Amiodarone', 'Fentanyl', 'Major', 'Coadministration with inhibitors of CYP450 3A4 such as amiodarone may increase the plasma concentrations of fentanyl, which is metabolized by the isoenzyme. Increased fentanyl concentrations could conceivably increase or prolong adverse drug effects and may cause potentially fatal respiratory depression. Amiodarone may increase the risk of cardiovascular complications during or after general anesthesia with fentanyl. The mechanism of interaction is unknown.', 'DDInter', 1767317474, 'Patients receiving fentanyl in combination with CYP450 3A4 inhibitors such as amiodarone should be carefully monitored, and dosage adjustments made accordingly if necessary. Patients and/or their caregivers should be advised to seek medical attention if potential signs and symptoms of toxicity occur such as dizziness, confusion, fainting, extreme sedation, bradycardia, slow or difficult breathing, and shortness of breath. Patients treated with transdermal formulations of fentanyl should be cautioned that drug interactions and drug effects may be observed for a prolonged period beyond removal of the patch, as significant amounts of fentanyl are absorbed from the skin for 17 hours or more after the patch is removed. Patients receiving fentanyl-containing anesthesia should be closely monitored for hemodynamic instability and other cardiovascular complications, and appropriate measures taken as required.', 'Metabolism', 'Patients receiving fentanyl in combination with CYP450 3A4 inhibitors such as amiodarone should be carefully monitored, and dosage adjustments made accordingly if necessary.', NULL, NULL, 'Major', 'pharmacodynamic');
