INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1325913, 'Fentanyl', 'Blinatumomab', 'Moderate', 'Coadministration with blinatumomab may increase the plasma concentrations of drugs that are substrates of CYP450 isoenzymes. Initiation of blinatumomab treatment causes transient release of cytokines that may suppress CYP450 enzymes, although the potential for interaction has not been studied.', 'DDInter', 1767317474, 'Caution is advised when blinatumomab is prescribed to patients receiving drugs that are metabolized by CYP450 isoenzymes, particularly those with a narrow therapeutic index such as carbamazepine, colchicine, cyclosporine, disopyramide, phenytoin, quinidine, theophylline, warfarin, macrolide immunosuppressants, vinca alkaloids, and some narcotic analgesics. Clinical and laboratory monitoring are recommended following the initiation of blinatumomab, and the individual dosage of the concomitant agents adjusted as needed.', 'Metabolism', 'Caution is advised when blinatumomab is prescribed to patients receiving drugs that are metabolized by CYP450 isoenzymes, particularly those with a narrow therapeutic index such as carbamazepine, colchicine, cyclosporine, disopyramide, phenytoin, quinidine, theophylline, warfarin, macrolide immunosuppressants, vinca alkaloids, and some narcotic analgesics.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1325914, 'Fentanyl', 'Boceprevir', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of fentanyl, which is primarily metabolized by the isoenzyme. Increased fentanyl concentrations could increase or prolong adverse drug effects and may cause potentially fatal respiratory depression. Conversely, discontinuation of a CYP450 3A4 inhibitor could decrease fentanyl plasma concentrations, decrease opioid efficacy, and possibly even lead to a withdrawal syndrome in patients who had developed physical dependence to fentanyl.', 'DDInter', 1767317474, 'Patients receiving fentanyl with potent or moderate CYP450 3A4 inhibitors should be carefully monitored, and dosage adjustments made accordingly as needed. This is particularly important when an inhibitor is added after a stable dose of fentanyl has been achieved. Patients and/or their caregivers should be advised to seek medical attention if potential signs and symptoms of toxicity occur, such as dizziness, confusion, fainting, extreme sedation, unresponsiveness, bradycardia, slow or difficult breathing, and shortness of breath.', 'Metabolism', 'Patients receiving fentanyl with potent or moderate CYP450 3A4 inhibitors should be carefully monitored, and dosage adjustments made accordingly as needed.', NULL, NULL, 'Major', 'pharmacodynamic');
