INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (3638, 'Eluxadoline', 'Chlorpheniramine', 'Moderate', 'The risk of constipation and serious constipation-related adverse reactions may be increased when eluxadoline is used with other drugs that are also associated with this adverse effect.', NULL, 'Concomitant use of eluxadoline with other drugs that can cause constipation such as alosetron, anticholinergics, and opioids should generally be avoided.', NULL, 'Concomitant use of eluxadoline with other drugs that can cause constipation such as alosetron, anticholinergics, and opioids should generally be avoided. Loperamide may be used occasionally for acute management of severe diarrhea, but chronic use is not recommended, and it should be discontinued immediately if constipation occurs. Eluxadoline should also be discontinued if constipation occurs for more than 4 days.', 'Synergism', 'Ketotifen, Levocetirizine, Cetirizine, Loratadine, Trimethobenzamide, Desloratadine, Azelastine', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/3638/', '[1] "Product Information. Viberzi (eluxadoline)." Actavis Pharma, Inc., Parsippany, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Viberzi (eluxadoline)." Actavis Pharma, Inc., Parsippany, NJ.', 1767369485);
