INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (3676, 'Glycopyrronium (topical)', 'Chlorpheniramine', 'Moderate', 'The potential exists for additive anticholinergic effects such as mydriasis, blurred vision, heat intolerance, fever, dry mouth, tachycardia, urinary retention, constipation, and glaucoma (onset or exacerbation) when topical or inhaled anticholinergic agents are used with each other or with other agents that possess anticholinergic properties. The risk of systemic anticholinergic effects following topical administration depends on variables such as strength of the product, size of the application area, frequency of application, and use of occlusive dressing. Systemic effects are uncommon following oral inhalation or nasal administration due to the poor absorption of quaternary ammonium compounds from gastrointestinal and nasal mucosa.', NULL, 'Topical and inhaled anticholinergic preparations should preferably not be used in combination with other anticholinergic agents or agents with significant anticholinergic effects such as antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, and class IA antiarrhythmics (especially disopyramide).', NULL, 'Topical and inhaled anticholinergic preparations should preferably not be used in combination with other anticholinergic agents or agents with significant anticholinergic effects such as antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, and class IA antiarrhythmics (especially disopyramide). Caution is advised if concomitant use cannot be avoided, particularly in the elderly and those with significantly impaired renal and/or hepatic function.', 'Synergism', 'Ketotifen, Levocetirizine, Cetirizine, Fexofenadine, Loratadine, Trimethobenzamide, Desloratadine, Azelastine, Terfenadine, Astemizole', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/3676/', '[1] "Product Information. Qbrexza (glycopyrrolate topical)." Dermira, Inc., Menlo Park, CA.[2] "Product Information. Anoro Ellipta (umeclidinium-vilanterol)." GlaxoSmithKline, Research Triangle Park, NC.[3] "Product Information. Combivent (albuterol-ipratropium)." Boehringer-Ingelheim, Ridgefield, CT.[4] "Product Information. Atrovent (ipratropium)." Boehringer-Ingelheim, Ridgefield, CT.[5] "Product Information. Yupelri (revefenacin)." Mylan Specialty, Morgantown, WV.[6] "Product Information. Tudorza Pressair (aclidinium)." Forest Pharmaceuticals, St. Louis, MO.[7] "Product Information. Spiriva (tiotropium)." Boehringer Ingelheim, Ridgefield, CT.[8] Cole JM, Sheehan AH, Jordan JK "Concomitant use of ipratropium and tiotropium in chronic obstructive plmonary disease." Ann Pharmacother 46 (2012): 1717-21[9] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[10] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[11] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[12] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (3677, 'Guanadrel', 'Phenylephrine', 'Moderate', 'Sympathomimetic amines may decrease the hypotensive effect of postganglionic adrenergic blocking agents, while the latter may potentiate the pharmacologic effects of direct-acting sympathomimetic amines (e.g., dobutamine, epinephrine, methoxamine, norepinephrine) but inhibit those that are primarily indirect-acting (e.g., mephentermine). Postganglionic adrenergic blocking agents such as guanadrel, guanethidine, and rauwolfia alkaloids work by depleting catecholamine stores from adrenergic nerve endings. Therefore, they may sensitize adrenergic receptors to direct-acting sympathomimetics, but blunt the effects of indirect-acting agents whose activity is mediated through the release of catecholamines.', NULL, 'Due to their pressor effect, sympathomimetic amines should be used cautiously in patients with hypertension.', NULL, 'Due to their pressor effect, sympathomimetic amines should be used cautiously in patients with hypertension. Alternatives to postganglionic adrenergic blocking agents should be considered if patients are treated with sympathomimetic amines, since effects of the latter may be intensified or diminished depending on whether they are direct- or indirect-acting. Most agents with indirect sympathomimetic activity are mixed-acting, thus it may be difficult to predict how they will be affected by postganglionic adrenergic blocking agents. If the combination is used, blood pressure and heart rate should be monitored.', 'Antagonism', 'Ketorolac, Homatropine, Atropine, Scopolamine, Methscopolamine, Tropicamide, Ciclesonide, Mupirocin, Mometasone furoate, Mometasone, Flunisolide, More', NULL, 'Moderate', 