INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (3851, 'Propranolol', 'Phenylephrine', 'Moderate', 'A case report suggests that beta-blockers may enhance the pressor response to phenylephrine. The proposed mechanism involves blockade of beta-2 adrenergic receptors in the peripheral vasculature, resulting in unopposed alpha-adrenergic effect of phenylephrine that is responsible for vasoconstriction. Additionally, beta-blockers may desensitize baroreceptors that normally modulate heart rate in response to blood pressure elevations by increasing vagal activity on the sinoauricular node.', NULL, 'Until more information is available, caution should be exercised when phenylephrine is used in combination with beta-blockers including ophthalmic formulations, which may be systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels.', NULL, 'Until more information is available, caution should be exercised when phenylephrine is used in combination with beta-blockers including ophthalmic formulations, which may be systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels. Monitoring of blood pressure should be considered, particularly when phenylephrine is administered intravenously or intraocularly.', 'Synergism', 'Tropicamide, Ciclesonide, Mupirocin, Mometasone furoate, Mometasone, Flunisolide, Olopatadine, Vitamin A, Ipratropium, Cromoglicic acid, Hyaluronic acid, More', 'Felodipine, Amlodipine, Nifedipine, Ivabradine, Acetylsalicylic acid', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/3851/', '[1] Myers MG, Iazzetta JJ "Intranasally administered