INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (3996, 'Amisulpride', 'Acebutolol', 'Moderate', 'Amisulpride can cause dose- and concentration-dependent prolongation of the QT interval. The concomitant use of amisulpride with agents that can cause bradycardia may theoretically potentiate the risk of QT interval prolongation and torsade de pointes arrhythmia.', NULL, 'Caution and clinical monitoring (e.', NULL, 'Caution and clinical monitoring (e.g., electrocardiogram, serum electrolytes) are recommended if amisulpride is used in combination with other drugs that can cause bradycardia. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Droperidol, Pimozide, Pimavanserin', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/3996/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Barhemsys (amisulpride)." Acacia Pharma, Inc, Indianapolis, IN.[3] Bailey DG, Dresser GK "Natural products and adverse drug interactions." Can Med Assoc J 170 (2004): 1531-2[4] Ho PC, Ghose K, Saville D, Wanwimolruk S "Effect of grapefruit juice on pharmacokinetics and pharmacodynamics of verapamil enantiomers in healthy volunteers." Eur J Clin Pharmacol 56 (2000): 693-8[5] Fuhr U, Muller-Peltzer H, Kern R, et al. "Effects of grapefruit juice and smoking on verapamil concentrations in steady state." Eur J Clin Pharmacol 58 (2002): 45-53[6] Arayne MS, Sultana N, Bibi Z "Review: grape fruit juice - drug interactions." P