INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8051, 'Diatrizoate', 'Ketorolac', 'Major', 'Concomitant use of intravascular radiocontrast media with other nephrotoxic agents may potentiate the risk of contrast-induced nephropathy and renal impairment. While the condition is usually transient and asymptomatic, it can be associated with increased risk of renal failure, dialysis, prolonged hospitalization, significant long-term morbidity, and mortality.', NULL, 'Alternative imaging techniques that do not require contrast should be considered in patients who are at increased risk for contrast-induced nephropathy.', NULL, 'Alternative imaging techniques that do not require contrast should be considered in patients who are at increased risk for contrast-induced nephropathy. Any repeat procedures with contrast media, if necessary, should not occur until at least 48 to 72 hours after the previous contrast exposure and renal function has fully recovered.', 'Synergism', 'Ethiodized oil, Iopanoic acid, Tyropanoic acid, Iopodic acid', 'Rabeprazole, Misoprostol, Chondroitin sulfate, Glucosamine, Esomeprazole, Homatropine, Atropine, Scopolamine, Methscopolamine, Ephedrine, Tropicamide, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8055/', '[1] Dubrovskaya Y, Prasad N, Lee Y, Esaian D, Figueroa DA, Tam VH "Risk factors for nephrotoxicity onset associated with polymyxin B therapy." J Antimicrob Chemother 70 (2015): 1903-7[2] Bentley ML, Corwin HL, Dasta J "Drug-induced acute kidney injury in the critically ill adult: recognition and prevention strategies." Crit Care Med 38(6 Suppl) (2010): S169-74[3] Kellum JA, Leblanc M, Venkataraman R "Acute renal failure." BMJ Cli