INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8052, 'Diclofenac', 'Ketorolac', 'Major', 'The use of ketorolac in combination with other nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the risk of serious adverse effects such as renal failure and gastrointestinal toxicity including inflammation, bleeding, ulceration, and perforation of the esophagus, stomach, or intestines.', NULL, 'Concurrent use of ketorolac with other NSAIDs or aspirin is considered contraindicated.', NULL, 'Concurrent use of ketorolac with other NSAIDs or aspirin is considered contraindicated.', 'Synergism', 'Glucosamine, Chondroitin sulfate, Esomeprazole, Diclofenac, Homatropine, Methscopolamine, Ephedrine, Scopolamine, Tropicamide, Phenylephrine, Fluorometholone, More', 'Fluocinolone acetonide, Fluorometholone, Fluorometholone, Fluocinolone acetonide, Ketorolac, Glucosamine, Chondroitin sulfate, Esomeprazole, Ketorolac, Tolazoline, Dimethyl sulfoxide, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8056/', '[1] "Product Information. Toradol (ketorolac)." Roche Laboratories, Nutley, NJ.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8053, 'Diclofenac (topical)', 'Ketorolac', 'Moderate', 'Nonsteroidal anti-inflammatory drugs may be absorbed systemically following topical administration. Concomitant use with systemic nonsteroidal anti-inflammatory drugs may result in increased adverse effects such as gastrointestinal toxicity (inflammation, bleeding, ulceration, perforation), cardiovascular thrombotic events, hepatotoxicity (liver enzyme elevations), renal toxicity, fluid retention, edema, hypertension, and inhibition of platelet aggregation.', NULL, 'Patients treated with topical preparations of nonsteroidal anti-inflammatory drugs should avoid or limit the use of systemic nonsteroidal anti-inflammatory drugs.', NULL, 'Patients treated with topical preparations of nonsteroidal anti-inflammatory drugs should avoid or limit the use of systemic nonsteroidal anti-inflammatory drugs. Although systemic exposure is generally low following topical administration, absorption may be increased with frequent applications; use of large quantities or over large areas of skin; use on compromised or diseased skin or open wounds; and use of occlusive dressings or heating pads over application areas.', 'Synergism', 'Rabeprazole, Misoprostol, Chondroitin sulfate, Glucosamine, Esomeprazole, Homatropine, Atropine, Scopolamine, Methscopolamine, Ephedrine, Tropicamide, More', 'Fluocinolone acetonide, Fluorometholone, Fluorometholone, Fluocinolone acetonide, Ketorolac, Glucosamine, Chondroitin sulfate, Esomeprazole, Ketorolac, Tolazoline, Dimethyl sulfoxide, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8057/', '[1] "Product Information. Solaraze (diclofenac topical)." Doak Dermatologics Division, Fairfield, NJ.', 1767369485);
