INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8208, 'Prasugrel', 'Ketorolac', 'Major', 'Concomitant use of prasugrel with other agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, dextran, nonsteroidal anti-inflammatory agents (chronic), or agents that commonly cause thrombocytopenia may increase the risk of bleeding. Prasugrel may be used with aspirin, heparin, or glycoprotein IIb/IIIa inhibitors.', NULL, 'Caution is advised if prasugrel is used in combination with other drugs that affect hemostasis.', NULL, 'Caution is advised if prasugrel is used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hemorrhagic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Misoprostol, Chondroitin sulfate, Glucosamine, Homatropine, Atropine, Scopolamine, Methscopolamine, Ephedrine, Tropicamide, Phenylephrine, Fluocinolone acetonide, More', 'Protein C, Acenocoumarol, Selexipag', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8212/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Effient (prasugrel)." Lilly, Eli and Company, Indianapolis, IN.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8209, 'Prazosin', 'Ketorolac', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may decrease the antihypertensive effect of prazosin. The proposed mechanism is NSAID-induced inhibition of prostaglandin synthesis. Data are available for indomethacin. A similar interaction is expected with other NSAIDs.', NULL, 'The patient''s blood pressure should be monitored during coadministration, and the dosage adjusted as necessary.', NULL, 'The patient''s blood pressure should be monitored during coadministration, and the dosage adjusted as necessary.', 'Antagonism', 'Rauwolfia serpentina root, Reserpine, Guanethidine, Deserpidine, Doxazosin, Guanethidine', 'Rabeprazole, Phenylbutazone, Misoprostol, Glucosamine, Chondroitin sulfate, Esomeprazole, Valdecoxib, Homatropine, Atropine, Scopolamine, Methscopolamine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8213/', '[1] Radack KL, Deck CC, Bloomfield SS "Ibuprofen interferes with the efficacy of antihypertensive drugs: a randomized, double-blind, placebo-controlled trial of ibuprofen compared with acetaminophen." Ann Intern Med 107 (1987): 628-35[2] Rubin P, Jackson G, Blaschke T "Studies on the clinical pharmacology of prazosin. II: The influence of indomethacin and of propranolol on the action and disposition of prazosin." Br J Clin Pharmacol 10 (1980): 33-9', 1767369485);
