INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8393, 'Acyclovir', 'Theophylline', 'Minor', 'Limited data suggest that the combination of a methyl xanthine with acyclovir may lead to elevated theophylline levels potentiating pharmacologic and adverse effects. The mechanism may be due to inhibition of theophylline oxidative metabolism. Clinical and laboratory monitoring is warranted if these agents are given concurrently. Patients receiving this combination should be advised to report any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat to their physician.', NULL, '-', NULL, '-', 'Metabolism', 'Omalizumab, Tezepelumab, Benralizumab, Caffeine, Reslizumab, Dyphylline, Mepolizumab, Roflumilast, Bromotheophylline', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Telaprevir, More', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8397/', '[1] Maeda Y, Konishi T, Omoda K, Takeda Y, Fukuhara S, Fukuzawa M, Ohune T, Tsuya T, Tsukiai S "Inhibition of theophylline metabolism by aciclovir." Biol Pharm Bull 19 (1996): 1591-5[2] Sato J, Nakata H, Owada E, Kikuta T, Umetsu M, Ito K "Influence of usual intake of dietary caffeine on single-dose kinetics of theophylline in healthy human subjects." Eur J Clin Pharmacol 44 (1993): 295-8[3] Jonkman JH, Sollie FA, Sauter R, Steinijans VW "The influence of caffeine on the steady-state pharmacokinetics of theophylline." Clin Pharmacol Ther 49 (1991): 248-55[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with co