INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8627, 'Avanafil', 'Nifedipine', 'Moderate', 'Phosphodiesterase-5 (PDE5) inhibitors may potentiate the blood pressure-lowering effect of antihypertensive medications. These agents inhibit PDE5-mediated degradation of cyclic guanosine monophosphate (cGMP), which in vascular smooth muscles can lead to peripheral vasodilation.', NULL, 'Caution is advised if PDE5 inhibitors are prescribed in combination with antihypertensive agents.', NULL, 'Caution is advised if PDE5 inhibitors are prescribed in combination with antihypertensive agents. Patients receiving the combination should be advised to avoid rising abruptly from a sitting or recumbent position, especially following treatment initiation or a dosage increase, and to contact their doctor if they experience symptoms of hypotension such as dizziness, lightheadedness, fainting, or tachycardia.', 'Synergism', 'Celecoxib, Acetylsalicylic acid, Ivabradine', 'Pentosan polysulfate, Phenazopyridine, Acetohydroxamic acid, Trospium, Yohimbine, Flavoxate, Darifenacin, Papaverine, Magnesium hydroxide, Ammonium chloride, Dimethyl sulfoxide', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8631/', '[1] "Product Information. Stendra (avanafil)." Vivus Inc, Mountain View, CA.[2] "Product Information. Viagra (sildenafil)." Pfizer US Pharmaceuticals, New York, NY.[3] "Product Information. Levitra (vardenafil)." Bayer, West Haven, CT.[4] Jetter A, Kinzig-Schippers M, Walchner-Bonjean M, et al. "Effects of grapefruit juice on the pharmacokinetics of sildenafil." Clin Pharmacol Ther 71 (2002): 21-29', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8628, 'Avapritinib', 'Nifedipine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of avapritinib, which has been shown in vitro to be primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2C9.', NULL, 'Caution is advised when avapritinib is used concomitantly with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when avapritinib is used concomitantly with CYP450 3A4 inhibitors. Patients should be closely monitored for development of serious adverse effects such as intracranial hemorrhage, cognitive impairment, mood disorders, hallucinations, edema, and decreases in hemoglobin, leukocytes, neutrophils and platelets, and the dosing of avapritinib adjusted or withheld as necessary in accordance with the product labeling.', 'Metabolism', 'Felodipine, Nisoldipine, Nicardipine, Isradipine, Clevidipine, Celecoxib, Nimodipine, Felodipine, Propranolol, Metoprolol, Carvedilol, More', 'Dacomitinib, Tepotinib, Vemurafenib, Vandetanib, Capmatinib, Afatinib, Tivozanib, Sunitinib, Regorafenib, Alpelisib, Lenvatinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8632/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Ayvakit (avapritinib)." Blueprint Medicines Corporation, Cambridge, MA.[3] "Product Information. Ayvakit (avapritinib)." Blueprint Medicines Corporation, Cambridge, MA.[4] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8629, 'Axitinib', 'Nifedipine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of axitinib, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised if axitinib is prescribed with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if axitinib is prescribed with CYP450 3A4 inhibitors. Alternative agents with no or minimal CYP450 3A4 inhibition potential are recommended whenever possible. Otherwise, patients should be monitored closely for development of toxicity such as hypertension/hypertensive crisis, arterial and venous thromboembolic complications, hemorrhage, gastrointestinal perforation or fistula, thyroid dysfunction, reversible posterior leukoencephalopathy syndrome, proteinuria, and liver enzyme elevations or hepatic impairment.', 'Metabolism', 'Felodipine, Nisoldipine, Nicardipine, Isradipine, Clevidipine, Celecoxib, Nimodipine, Felodipine, Propranolol, Metoprolol, Carvedilol, More', 'Dacomitinib, Tepotinib, Vemurafenib, Vandetanib, Capmatinib, Afatinib, Tivozanib, Sunitinib, Regorafenib, Alpelisib, Lenvatinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8633/', '[1] "Product Information. Inlyta (axitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Inlyta (axitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767369485);
