INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8901, 'Nifedipine', 'Nefazodone', 'Moderate', 'Coadministration with nefazodone may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by nefazodone.', NULL, 'Caution is advised if nefazodone must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if nefazodone must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. A lower initial dosage, as well as clinical and laboratory monitoring, may be appropriate for some drugs.', 'Metabolism', 'Desipramine, Vilazodone, Trimipramine, Desvenlafaxine, Maprotiline, Oxitriptan, Tryptophan, Vortioxetine, Milnacipran, Levomilnacipran, Amoxapine, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8905/', '[1] "Product Information. Serzone (nefazodone)." Bristol-Myers Squibb, Princeton, NJ.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8902, 'Nifedipine', 'Nelfinavir', 'Moderate', 'Coadministration with nelfinavir may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by nelfinavir.', NULL, 'Caution is advised if nelfinavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if nelfinavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever nelfinavir is added to or withdrawn from therapy.', 'Metabolism', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Velpatasvir, Oseltamivir, Brincidofovir, Remdesivir, Elbasvir, Tipranavir, More', 'Clevidipine, Celecoxib, Propranolol, Metoprolol, Carvedilol, Nebivolol, Bisoprolol, Sotalol, Acetylsalicylic acid, Atenolol', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8906/', '[1] "Product Information. Viracept (nelfinavir)." Agouron Pharma Inc, La Jolla, CA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8903, 'Nifedipine', 'Nevirapine', 'Moderate', 'Coadministration with nevirapine may decrease the plasma concentrations and therapeutic efficacy of drugs that are substrates of CYP450 3A4. The proposed mechanism is increased clearance due to nevirapine-mediated induction of CYP450 3A4.', NULL, 'Caution is advised if nevirapine is used concomitantly with drugs that are CYP450 3A4 substrates.', NULL, 'Caution is advised if nevirapine is used concomitantly with drugs that are CYP450 3A4 substrates. Dosage adjustments as well as clinical and laboratory monitoring of coadministered drugs should be considered whenever nevirapine is added to or withdrawn from therapy.', 'Metabolism', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Velpatasvir, Oseltamivir, Brincidofovir, Remdesivir, Elbasvir, Tipranavir, More', 'Clevidipine, Nisoldipine, Celecoxib, Nebivolol, Bisoprolol, Sotalol', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8907/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Viramune (nevirapine)." Boehringer-Ingelheim, Ridgefield, CT.[3] "Product Information. Viramune XR (nevirapine)." Boehringer Ingelheim, Ridgefield, CT.[4] Cerner Multum, Inc. "Australian Product Information." O 0', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8904, 'Nifedipine', 'Nilotinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein may increase the plasma concentrations of nilotinib, which is a substrate of both the isoenzyme and the efflux transporter. Data are not available for nilotinib in combination with other CYP450 3A4 or P-glycoprotein inhibitors. Theoretically, a reverse interaction may also occur, since many CYP450 3A4 and P-glycoprotein inhibitors are also substrates, and nilotinib is an inhibitor of both.', NULL, 'Caution is advised if nilotinib is prescribed in combination with CYP450 3A4 and/or P-glycoprotein inhibitors.', NULL, 'Caution is advised if nilotinib is prescribed in combination with CYP450 3A4 and/or P-glycoprotein inhibitors. Pharmacologic response to nilotinib should be monitored more closely whenever a CYP450 3A4 or P-glycoprotein inhibitor is added to or withdrawn from therapy, and the nilotinib dosage adjusted as necessary. Patients should have frequent ECGs and be monitored for arrhythmias when QT interval is prolonged. A QTc interval exceeding 480 msec will require suspension of nilotinib therapy and immediate action to correct any concomitant risk factors before resuming treatment. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. Patients should also be monitored for altered efficacy and safety of the concomitant administered drug.', 'Metabolism', 'Clevidipine, Nicardipine, Acetylsalicylic acid, Nebivolol, Bisoprolol, Atenolol', 'Dacomitinib, Tepotinib, Vemurafenib, Vandetanib, Capmatinib, Afatinib, Tivozanib, Sunitinib, Regorafenib, Alpelisib, Lenvatinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8908/', '[1] "Product Information. Tasigna (nilotinib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Tasigna (nilotinib)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8905, 'Nifedipine', 'Nintedanib', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and CYP450 3A4 may increase the plasma concentrations of nintedanib, which is a substrate of the efflux transporter and a minor substrate of the isoenzyme.', NULL, 'Caution is advised if nintedanib is prescribed in combination with P-gp and CYP450 3A4 inhibitors.', NULL, 'Caution is advised if nintedanib is prescribed in combination with P-gp and CYP450 3A4 inhibitors. Pharmacologic response to nintedanib should be monitored more closely whenever a CYP450 3A4/P-gp inhibitor is added to or withdrawn from therapy, and the nintedanib dosing adjusted or interrupted as necessary in accordance with the product labeling. Patients should be closely monitored for increased adverse effects such as liver enzyme and bilirubin elevations, diarrhea, nausea, vomiting, gastrointestinal perforation, bleeding, and arterial thromboembolic events (e.g., myocardial infarction).', 'Metabolism', 'Felodipine, Amlodipine, Nisoldipine, Nicardipine, Isradipine, Clevidipine, Levamlodipine, Celecoxib, Nimodipine, Amlodipine, Felodipine, More', 'Dacomitinib, Tepotinib, Vemurafenib, Vandetanib, Capmatinib, Afatinib, Tivozanib, Sunitinib, Regorafenib, Alpelisib, Lenvatinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8909/', '[1] "Product Information. Ofev (nintedanib)." Boehringer Ingelheim, Ridgefield, CT.[2] "Product Information. Ofev (nintedanib)." Boehringer Ingelheim, Ridgefield, CT.', 1767369485);
