INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8973, 'Ribociclib', 'Nifedipine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations and risk of adverse effects of ribociclib, which is a substrate of the isoenzyme. Administration of erythromycin, a moderate CYP450 3A4 inhibitor, is predicted to increase ribociclib Cmax and AUC by 1.3- and 1.9-fold, respectively.', NULL, 'Caution is advised when ribociclib is used with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when ribociclib is used with CYP450 3A4 inhibitors. Dosage adjustment for ribociclib is not generally considered necessary for suspected interactions with mild to moderate inhibitors. However, some manufacturers of ribociclib recommend that concomitant use with moderate CYP450 3A4 inhibitor verapamil should be avoided (AU, UK). Patients should be monitored for the development of ribociclib-related adverse effects such as infections, neutropenia, leucopenia, anemia, thrombocytopenia, nausea, vomiting, diarrhea, stomatitis, anorexia, alopecia, fatigue, headache, and abnormal liver function.', 'Metabolism', 'Clevidipine, Celecoxib, Propranolol, Metoprolol, Carvedilol, Nebivolol, Bisoprolol, Acetylsalicylic acid, Atenolol', 'Dacomitinib, Tepotinib, Vemurafenib, Vandetanib, Capmatinib, Afatinib, Tivozanib, Sunitinib, Regorafenib, Alpelisib, Lenvatinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8977/', '[1] "Product Information. Kisqali (ribociclib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Informa