INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8987, 'Rucaparib', 'Nifedipine', 'Moderate', 'Coadministration with rucaparib may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of CYP450 3A4. The proposed mechanism is decreased clearance due to rucaparib-mediated inhibition of CYP450 3A4.', NULL, 'Caution is advised if rucaparib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if rucaparib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever rucaparib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Clevidipine, Metoprolol, Carvedilol, Nebivolol, Bisoprolol, Acetylsalicylic acid, Atenolol', 'Pertuzumab, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Enasidenib, Niraparib, Selinexor, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8991/', '[1] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8988, 'Rufinamide', 'Nifedipine', 'Moderate', 'Rufinamide may modestly or moderately decrease the plasma concentrations and therapeutic effects of drugs that are CYP450 3A4 substrates. The mechanism is enhanced metabolism by CYP450 3A4 enzyme induction.', NULL, 'During concomitant treatment with rufinamide and drugs that are CYP450 3A4 substrates, it is recommended that patients should be monitored for 2 weeks when rufinamide is added to or withdrawn from therapy, or after increases of the dose.', NULL, 'During concomitant treatment with rufinamide and drugs that are CYP450 3A4 substrates, it is recommended that patients should be monitored for 2 weeks when rufinamide is added to or withdrawn from therapy, or after increases of the dose. Dose adjustments of the coadministered drug may be required if an interaction is suspected.', 'Metabolism', 'Felodipine, Nicardipine, Isradipine, Clevidipine, Nimodipine, Celecoxib, Mibefradil, Felodipine, Propranolol, Metoprolol, Carvedilol, More', 'Tiagabine, Brivaracetam, Cannabidiol, Methsuximide, Ezogabine, Paramethadione, Perampanel, Fenfluramine, Eslicarbazepine, Phensuximide, Phenacemide, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8992/', '[1] "Product Information. Banzel (rufinamide)." Eisai Inc, Teaneck, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Yamreudeewong W, Henann NE, Fazio A, Lower DL, Cassidy TG "Drug-food interactions in clinical practice." J Fam Pract 40 (1995): 376-84[4] "Product Information. Ambien (zolpidem)." sanofi-aventis, Bridgewater, NJ.[5] "Product Information. Banzel (rufinamide)." Eisai Inc, Teaneck, NJ.