INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (4291, 'Phenylephrine (ophthalmic)', 'Acebutolol', 'Moderate', 'A case report suggests that beta-blockers may enhance the pressor response to phenylephrine. The proposed mechanism involves blockade of beta-2 adrenergic receptors in the peripheral vasculature, resulting in unopposed alpha-adrenergic effect of phenylephrine that is responsible for vasoconstriction. Additionally, beta-blockers may desensitize baroreceptors that normally modulate heart rate in response to blood pressure elevations by increasing vagal activity on the sinoauricular node.', NULL, 'Until more information is available, caution should be exercised when phenylephrine is used in combination with beta-blockers including ophthalmic formulations, which may be systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels.', NULL, 'Until more information is available, caution should be exercised when phenylephrine is used in combination with beta-blockers including ophthalmic formulations, which may be systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels. Monitoring of blood pressure should be considered, particularly when phenylephrine is administered intravenously or intraocularly.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/4295/', '[1] Myers MG, Iazzetta JJ "Intranasally administered phenylephrine and blood pressure." Can Med Assoc J 127 (1982): 365-6[2] Cass E, Kadar D, Stein HA "Hazards of phenylephrine topical medication in persons taking propranolol." Can Med Assoc J 120 (1979): 1261-2', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (4292, 'Physostigmine', 'Acebutolol', 'Moderate', 'Severe bradycardia and hypotension may develop during concomitant administration of some beta-blockers and anticholinesterase agents. The mechanism is unknown but may involve additive effects.', NULL, 'The patient''s hemodynamic status should be monitored during coadministration.', NULL, 'The patient''s hemodynamic status should be monitored during coadministration. Patients should be advised to notify their physician if they experience syncope, orthostasis, or a slow pulse.', 'Others', 'Methazolamide, Acetylcholine, Betaxolol, Diclofenamide, Bimatoprost, Travoprost, Carteolol, Brinzolamide, Acetazolamide, Brimonidine, Timolol, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/4296/', '[1] Eldor J, Hoffman B, Davidson JT "Prolonged bradycardia and hypotension after neostigmine administration in a patient receiving atenolol." Anaesthesia 42 (1987): 1294-7', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (4293, 'Pilocarpine', 'Acebutolol', 'Moderate', 'The concomitant use of oral pilocarpine and beta blockers may lead to cardiac conduction disturbances. The mechanism is unknown.', NULL, 'If these drugs must be given together, consider the possibility for arrhythmias and administer with caution.', NULL, 'If these drugs must be given together, consider the possibility for arrhythmias and administer with caution. Patients should be advised to notify their physician if they experience irregular or slow heartbeats, palpitations, or syncope.', 'Others', 'Ambenonium, Bethanechol, Pyridostigmine, Methazolamide, Acetylcholine, Betaxolol, Diclofenamide, Bimatoprost, Travoprost, Carteolol, Brinzolamide, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/4297/', '[1] "Product Information. Salagen Tablets (pilocarpine)." Boehringer-Ingelheim, Ridgefield, CT.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (4294, 'Pipecuronium', 'Acebutolol', 'Moderate', 'Neuromuscular blocking agents and beta-blockers may have additive cardiovascular depressant effects. Limited data suggest that beta-blockers may modestly potentiate the pharmacologic effects of neuromuscular blocking agents. The exact mechanism of interaction is unknown but may involve effects in the postsynaptic membrane, as beta-blockers alone have been reported to exacerbate or unmask myasthenia gravis.', NULL, 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers.', NULL, 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers. Respiratory and cardiovascular status should be closely monitored.', 'Synergism', 'Carteolol', 'Botulinum toxin type A', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/4298/', '[1] Loan PB, Connolly FM, Mirakhur RK, Kumar N, Farling P "Neuromuscular effects of rocuronium in patients receiving beta-adrenoreceptor blocking, calcium entry blocking and anticonvulsant drugs." Br J Anaesth 78 (1997): 90-1[2] Harrah MD, Way WL, Katzung BG "The interaction of d-tubocurarine with antiarrhythmic drugs." Anesthesiology 33 (1970): 406-10[3] Rozen MS, Whan FM "Prolonged curarization associated with propranolol." Med J Aust 1 (1972): 467-8[4] Murthy VS, Patel KD, Elangovan RG, Hwang TF, Solochek SM, Steck JD, Laddu AR "Cardiovascular and neuromuscular effects of esmolol during induction of anesthesia." J Clin Pharmacol 26 (1986): 351-7[5] Glynne GL "Drug interaction?" Anaesthesia 39 (1984): 293[6] Herish