INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (4343, 'Stiripentol', 'Acebutolol', 'Moderate', 'The concomitant administration of stiripentol may significantly increase plasma concentrations, pharmacologic effects, and toxicities of drugs that are substrates of CYP450 2C19, 3A4, and/or 2D6. The mechanism is inhibition of cytochrome P-450 metabolism by stiripentol.', NULL, 'Clinical and laboratory monitoring for altered efficacy and safety is recommended during concomitant use.', NULL, 'Clinical and laboratory monitoring for altered efficacy and safety is recommended during concomitant use. Dosage adjustments may be required if increased plasma levels or effects are observed.', 'Metabolism', 'Nadolol, Esmolol, Betaxolol, Penbutolol, Bisoprolol, Carteolol, Sotalol, Pindolol, Atenolol, Nadolol, Bisoprolol, More', 'Brivaracetam, Topiramate, Ezogabine, Cenobamate, Lamotrigine, Phenytoin, Ethosuximide, Levetiracetam, Tiagabine, Oxcarbazepine, Ethotoin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/4347/', '[1] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[2] Yamreudeewong W, Henann NE, Fazio A, Lower DL, Cassidy TG "Drug-food interactions in clinical practice." J Fam Pract 40 (1995): 376-84[3] "Product Information. Ambien (zolpidem)." sanofi-aventis, Bridgewater, NJ.[4] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[5] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available