INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (4386, 'Trospium', 'Acebutolol', 'Moderate', 'Anticholinergic agents frequently cause drowsiness and other central nervous system-depressant effects that may be additive with those induced by beta blockers. In addition, these agents may increase heart rate and theoretically may counteract the bradycardic effects of beta blockers. Pharmacokinetically, anticholinergic agents may delay the gastrointestinal absorption of beta blockers and other drugs that are administered orally. The proposed mechanism involves increased gastrointestinal transit time due to reduction of stomach and intestinal motility by anticholinergic agents.', NULL, 'Patients should be monitored for potentially excessive CNS adverse effects (e.', NULL, 'Patients should be monitored for potentially excessive CNS adverse effects (e.g., drowsiness, dizziness, lightheadedness, confusion, blurred vision) if anticholinergic agents are used in combination with beta blockers. Patients should be counseled to avoid activities requiring mental alertness until they know how these agents affect them.', 'Synergism, Absorption', 'Fesoterodine, Pentosan polysulfate, Calcium chloride, Phenazopyridine, Alprostadil, Mirabegron, Acetohydroxamic acid, Yohimbine, Flavoxate, Darifenacin, Papaverine, More', 'Esmolol', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/4390/', '[1] Regardh CG, Lundborg P, Persson BA "The effect of antacid, metoclopramide, and propantheline on the bioavailability of metoprolol and atenolol." Biopharm Drug Dispos 2 (1981): 79-87[2] Briant RH, Dorrington RE, Ferry DG, Paxton JW "Bioavailability of metoprolol in young adults and the elderly, with additional studies on the effects of metoclopramide and probanthine." Eur J Clin Pharmacol 25 (1983): 353-6[3] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[4] Clark JM, Seager SJ "Gastric emptying following premedication with glycopyrrolate or atropine." Br J Anaesth 55 (1983): 1195-9', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (4387, 'Tubocurarine', 'Acebutolol', 'Moderate', 'Neuromuscular blocking agents and beta-blockers may have additive cardiovascular depressant effects. Limited data suggest that beta-blockers may modestly potentiate the pharmacologic effects of neuromuscular blocking agents. The exact mechanism of interaction is unknown but may involve effects in the postsynaptic membrane, as beta-blockers alone have been reported to exacerbate or unmask myasthenia gravis.', NULL, 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers.', NULL, 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers. Respiratory and cardiovascular status should be closely monitored.', 'Synergism', 'Carteolol', 'Botulinum toxin type A', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/4391/', '[1] Loan PB, Connolly FM, Mirakhur RK, Kumar N, Farling P "Neuromuscular effects of rocuronium in patients receiving beta-adrenoreceptor blocking, calcium entry blocking and anticonvulsant drugs." Br J Anaesth 78 (1997): 90-1[2] Harrah MD, Way WL, Katzung BG "The interaction of d-tubocurarine with antiarrhythmic drugs." Anesthesiology 33 (1970): 406-10[3] Rozen MS, Whan FM "Prolonged curarization associated with propranolol." Med J Aust 1 (1972): 467-8[4] Murthy VS, Patel KD, Elangovan RG, Hwang TF, Solochek SM, Steck JD, Laddu AR "Cardiovascular and neuromuscular effects of esmolol during induction of anesthesia." J Clin Pharmacol 26 (1986): 351-7[5] Glynne GL "Drug interaction?" Anaesthesia 39 (1984): 293[6] Herish