INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (4441, 'Amobarbital', 'Acetaminophen', 'Moderate', 'Barbiturates may increase the hepatotoxic potential of acetaminophen and decrease its therapeutic effects. The mechanism may be related to accelerated CYP450 metabolism of acetaminophen with consequent increase in hepatotoxic metabolites. This interaction is of greatest concern in cases of acetaminophen overdose.', NULL, 'Monitoring for altered efficacy and safety is recommended.', NULL, 'Monitoring for altered efficacy and safety is recommended. Prolonged use or high doses of acetaminophen should be avoided by patients on barbiturate therapy.', 'Metabolism', 'Salicylic acid, Choline salicylate, Diflunisal, Salsalate, Acetylsalicylic acid, Acetylsalicylic acid, Naloxone, Ibuprofen, Naltrexone', 'Remimazolam, Suvorexant, Chloral hydrate, Paraldehyde, Propiomazine, Ramelteon, Eszopiclone, Dexmedetomidine, Lemborexant, Ethchlorvynol, Tasimelteon, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/4445/', '[1] Wright N, Prescott LF "Potentiation by previous drug therapy of hepatotoxicity following paracetamol overdose." Scott Med J 18 (1973): 56-8[2] Pirotte JH "Apparent potentiation by phenobarbital of hepatotoxicity from small doses of acetaminophen." Ann Intern Med 101 (1984): 403[3] Douidar SM, Ahmed AE "A novel mechanism for the enhancement of acetaminophen hepatotoxicity by phenobarbital." J Pharmacol Exp Ther 240 (1987): 578-83[4] Bock KW, Wiltfang J, Blume R, Ullrich D, Bircher J "Paracetamol as a test drug to determine glucuronide formation in man: effects of inducers and of smoking." Eur J Clin Pharmacol 31 (1987): 677-83', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (4442, 'Anagrelide', 'Acetaminophen', 'Moderate', 'Theoretically, coadministration with anagrelide may increase the plasma concentrations and the risk of toxicity of drugs that are substrates of CYP450 1A2. The proposed mechanism, based on in vitro data, is decreased clearance due to anagrelide-mediated inhibition of CYP450 1A2 metabolism.', NULL, 'Until more information is available, caution is advised if anagrelide is used concomitantly with drugs that are substrates of CYP450 1A2, particularly those with a narrow therapeutic range.', NULL, 'Until more information is available, caution is advised if anagrelide is used concomitantly with drugs that are substrates of CYP450 1A2, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever anagrelide is added to or withdrawn from therapy. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Pertuzumab, Olaparib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Ixazomib, Sotorasib, Enasidenib, More', 'Salicylic acid, Salsalate, Choline salicylate, Ziconotide, Methoxyflurane, Codeine, Morphine, Naltrexone, Oxymorphone, Tapentadol, Oxycodone, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/4446/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Agrylin (anagrelide)." Roberts Pharmaceutical Corporation, Eatontown, NJ.[4] "Product Information. Ambien (zolpidem)." sanofi-