INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (4461, 'Ceritinib', 'Acetaminophen', 'Moderate', 'Coadministration with ceritinib may increase the plasma concentration and the risk of adverse effects of drugs that are substrates of CYP450 2A6 and/or 2E1. The proposed mechanism, based on in vitro data, is decreased clearance due to ceritinib-mediated inhibition of CYP450 2A6 and 2E1.', NULL, 'Caution is advised if ceritinib is used concomitantly with drugs that are substrates of CYP450 2A6 and/or 2E1, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if ceritinib is used concomitantly with drugs that are substrates of CYP450 2A6 and/or 2E1, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever ceritinib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Salicylic acid, Choline salicylate, Ziconotide, Diflunisal, Salsalate, Acetylsalicylic acid, Codeine, Oxymorphone, Tapentadol, Meperidine, Butorphanol, More', 'Entrectinib, Dabrafenib, Asciminib, Acalabrutinib, Dacomitinib, Pacritinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Bosutinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/4465/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing