INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (4491, 'Idelalisib', 'Acetaminophen', 'Moderate', 'Coadministration of idelalisib with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. The use of idelalisib has been associated with elevations in alanine aminotransferase (ALT) and aspartate aminotransferase (AST) greater than 5 times the upper limit of normal.', NULL, 'The use of idelalisib with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, 'The use of idelalisib with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; amiodarone; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; methotrexate; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; tetracyclines; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients treated with idelalisib should have serum ALT, AST, and bilirubin measured prior to initiation of treatment and regularly during treatment in accordance with the product labeling, and the dosing adjusted or interrupted as necessary.', 'Synergism', 'Salicylic acid, Choline salicylate, Ziconotide, Diflunisal, Methoxyflurane, Salsalate, Acetylsalicylic acid, Morphine, Oxymorphone, Tapentadol, Meperidine, More', 'Entrectinib, Dabrafenib, Asciminib, Acalabrutinib, Dacomitinib, Pacritinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Bosutinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/4495/', '[1] "Product Information. Zydelig (idelalisib)." Gilead Sciences, Foster City, CA.[2] "Product Information. Hivid (zalcitabine)." Roche Laboratories, Nutley, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (4492, 'Imatinib', 'Acetaminophen', 'Moderate', 'In vitro inhibition data suggest that coadministration with imatinib may increase the plasma concentrations of acetaminophen. The proposed mechanism is imatinib inhibition of acetaminophen clearance via O-glucuronidation. However, no specific pharmacokinetic studies in humans have been performed. Pharmacodynamically, the potential may exist for additive adverse effects on the liver, since both agents individually are associated with hepatotoxicity.', NULL, 'Caution is advised if imatinib is used in combination with acetaminophen, particularly at higher dosages of the latter.', NULL, 'Caution is advised if imatinib is used in combination with acetaminophen, particularly at higher dosages of the latter. All patients treated with imatinib should have liver function tests (transaminases, bilirubin, and alkaline phosphatase) prior to initiation of therapy and monthly thereafter or as clinically indicated. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, and jaundice. In addition, they should not take over-the-counter medications such as analgesics containing acetaminophen, flu preparations, or herbal products without first talking to their healthcare provider.', 'Synergism', 'Salicylic acid, Choline salicylate, Ziconotide, Diflunisal, Methoxyflurane, Salsalate, Tapentadol, Oxymorphone, Meperidine, Dezocine, Opium, More', 'Entrectinib, Dabrafenib, Asciminib, Acalabrutinib, Dacomitinib, Pacritinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Bosutinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/4496/', '[1] "Product Information. Gleevec (imatinib mesylate)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Lin NU, Sarantopoulos S, Stone JR, et al. "Fatal hepatic necrosis following imatinib mesylate therapy." Blood 102 (2003): 3455-6[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] "Product Information. Gleevec (imatinib mesylate)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
