INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (4827, 'Caffeine', 'Nalidixic acid', 'Moderate', 'Coadministration with certain quinolones may increase the plasma concentrations and pharmacologic effects of caffeine due to inhibition of the CYP450 1A2 metabolism of caffeine. Quinolones that may inhibit CYP450 1A2 include ciprofloxacin, enoxacin, grepafloxacin, nalidixic acid, norfloxacin, pipemidic acid, and pefloxacin (not all commercially available).', NULL, 'Patients using caffeine-containing products should be advised that increased adverse effects such as headache, tremor, restlessness, nervousness, insomnia, tachycardia, and blood pressure increases may occur during coadministration with quinolones that inhibit CYP450 1A2.', NULL, 'Patients using caffeine-containing products should be advised that increased adverse effects such as headache, tremor, restlessness, nervousness, insomnia, tachycardia, and blood pressure increases may occur during coadministration with quinolones that inhibit CYP450 1A2. Caffeine intake should be limited when taking high dosages of these quinolones. If an interaction is suspected, other quinolones such as gatifloxacin, gemifloxacin, levofloxacin, lomefloxacin, moxifloxacin, and ofloxacin may be considered, since they are generally believed to have little or no effect on CYP450 1A2 or have been shown not to interact with caffeine.', 'Metabolism', 'Minoxidil, Deoxycholic acid, Pimecrolimus, Glycopyrronium, Aminobenzoic acid, Finasteride, Ivermectin, Cromoglicic acid, Abrocitinib, Dupilumab, Tralokinumab, More', 'Gatifloxacin, Levofloxacin, Gemifloxacin, Lomefloxacin, Moxifloxacin, Cinoxacin, Delafloxacin, Sparfloxacin, Ofloxacin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic'