INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1326428, 'Acalabrutinib', 'Bexarotene', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of acalabrutinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317474, 'The potential for diminished pharmacologic effects of acalabrutinib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of acalabrutinib should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1326429, 'Acalabrutinib', 'Bicalutamide', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of acalabrutinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317474, 'Caution is advised when acalabrutinib is used with CYP450 3A4 inhibitors. Patients should be monitored for increased adverse effects such as nausea, vomiting, diarrhea, hemorrhage, infection, cytopenias, and atrial fibrillation or flutter, and the acalabrutinib dosage adjusted as necessary.', 'Metabolism', 'Caution is advised when acalabrutinib is used with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1326430, 'Acalabrutinib', 'Bifidobacterium longum infantis', 'Moderate', 'Probiotic use during immunosuppressant or intense antineoplastic therapy may theoretically increase the risk of infections from the live microorganisms contained in probiotic products. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317474, 'Caution is advised when probiotics are used during immunosuppressant or intense antineoplastic therapy. It may be advisable to avoid using probiotics, particularly products containing saccharomyces boulardii, in patients who are significantly immunosuppressed.', 'Others', 'Caution is advised when probiotics are used during immunosuppressant or intense antineoplastic therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
