INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1326519, 'Acalabrutinib', 'Famotidine', 'Moderate', 'INTERVAL: Coadministration with drugs that increase gastric pH may significantly decrease the oral bioavailability of acalabrutinib and reduce its concentrations in plasma. The solubility of acalabrutinib is pH-dependent and decreases with increasing pH.', 'DDInter', 1767317474, 'Concomitant use of acalabrutinib with proton pump inhibitors should generally be avoided. If acid suppression therapy is required, H2-receptor antagonists or antacids should be considered. The manufacturer recommends taking acalabrutinib 2 hours before H2-receptor antagonists and separating the dosing of antacids by at least 2 hours.', 'Absorption', 'Concomitant use of acalabrutinib with proton pump inhibitors should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
