INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1326597, 'Acalabrutinib', 'Miconazole', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of acalabrutinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317474, 'Caution is advised when acalabrutinib is used with CYP450 3A4 inhibitors. Patients should be monitored for increased adverse effects such as nausea, vomiting, diarrhea, hemorrhage, infection, cytopenias, and atrial fibrillation or flutter, and the acalabrutinib dosage adjusted as necessary.', 'Metabolism', 'Caution is advised when acalabrutinib is used with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1326598, 'Acalabrutinib', 'Mifepristone', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of acalabrutinib, which is primarily metabolized by the isoenzyme. Increased acalabrutinib exposure may potentiate the risk of toxicities such as hemorrhage, infection, cytopenias, malignancies, and atrial fibrillation or flutter.', 'DDInter', 1767317474, 'Concomitant use of acalabrutinib with potent CYP450 3A4 inhibitors should generally be avoided, particularly those that are intended for chronic administration. Alternative agents with no or minimal CYP450 3A4 inhibitory potential are recommended whenever possible. If no alternatives exist and the CYP450 3A4 inhibitor is used short-term for 7 days or less, consider interrupting or delaying initiation of acalabrutinib treatment until therapy with the inhibitor is complete.', 'Metabolism', 'Concomitant use of acalabrutinib with potent CYP450 3A4 inhibitors should generally be avoided, particularly those that are intended for chronic administration.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1326599, 'Acalabrutinib', 'Milnacipran', 'Moderate', 'Serotonin reuptake inhibitors (SRIs) may potentiate the risk of bleeding in patients treated with ulcerogenic agents and agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The tricyclic antidepressant, clomipramine, is also a strong SRI and may interact similarly. Serotonin release by platelets plays an important role in hemostasis, thus SRIs may alter platelet function and induce bleeding.', 'DDInter', 1767317474, 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1326600, 'Acalabrutinib', 'Mitotane', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of acalabrutinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317474, 'Concomitant use of acalabrutinib with potent CYP450 3A4 inducers should generally be avoided. If coadministration cannot be avoided, the manufacturer recommends increasing the dosage of acalabrutinib to 200 mg twice daily.', 'Metabolism', 'Concomitant use of acalabrutinib with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
