INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1326645, 'Acalabrutinib', 'Posaconazole', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of acalabrutinib, which is primarily metabolized by the isoenzyme. Increased acalabrutinib exposure may potentiate the risk of toxicities such as hemorrhage, infection, cytopenias, malignancies, and atrial fibrillation or flutter.', 'DDInter', 1767317474, 'Concomitant use of acalabrutinib with potent CYP450 3A4 inhibitors should generally be avoided, particularly those that are intended for chronic administration. Alternative agents with no or minimal CYP450 3A4 inhibitory potential are recommended whenever possible. If no alternatives exist and the CYP450 3A4 inhibitor is used short-term for 7 days or less, consider interrupting or delaying initiation of acalabrutinib treatment until therapy with the inhibitor is complete.', 'Metabolism', 'Concomitant use of acalabrutinib with potent CYP450 3A4 inhibitors should generally be avoided, particularly those that are intended for chronic administration.', NULL, NULL, 'Major', 'pharmacodynamic');
