INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1326649, 'Acalabrutinib', 'Radium Ra 223 dichloride', 'Moderate', 'The concomitant use of bone marrow depressants and radium Ra 223 dichloride may have additive myelosuppressive effects.', 'DDInter', 1767317474, 'Patients should be monitored for excessive bone marrow suppression during treatment with radium Ra 223 dichloride. Dose reductions of the other bone marrow depressants may be necessary. Patients should be advised to contact their physician if they develop signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, easy bruising or bleeding, or signs of infection such as fever, chills, sore throat, body aches, and other influenza-like symptoms. The safety and efficacy of concomitant use of chemotherapy have not been established.', 'Synergism', 'Patients should be monitored for excessive bone marrow suppression during treatment with radium Ra 223 dichloride.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1326650, 'Acalabrutinib', 'Ramucirumab', 'Major', 'Coadministration of ramucirumab and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Ramucirumab can cause bleeding, including severe and sometimes fatal hemorrhagic complications.', 'DDInter', 1767317474, 'Concomitant use of ramucirumab and medications that interfere with platelet function or coagulation should be approached cautiously. Close clinical and laboratory observation for bleeding complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools. Ramucirumab should be permanently discontinued in patients who experience severe bleeding.', 'Synergism', 'Concomitant use of ramucirumab and medications that interfere with platelet function or coagulation should be approached cautiously.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1326651, 'Acalabrutinib', 'Ranitidine', 'Moderate', 'INTERVAL: Coadministration with drugs that increase gastric pH may significantly decrease the oral bioavailability of acalabrutinib and reduce its concentrations in plasma. The solubility of acalabrutinib is pH-dependent and decreases with increasing pH.', 'DDInter', 1767317474, 'Concomitant use of acalabrutinib with proton pump inhibitors should generally be avoided. If acid suppression therapy is required, H2-receptor antagonists or antacids should be considered. The manufacturer recommends taking acalabrutinib 2 hours before H2-receptor antagonists and separating the dosing of antacids by at least 2 hours.', 'Absorption', 'Concomitant use of acalabrutinib with proton pump inhibitors should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1326652, 'Acalabrutinib', 'Ranitidine (bismuth citrate)', 'Moderate', 'INTERVAL: Coadministration with drugs that increase gastric pH may significantly decrease the oral bioavailability of acalabrutinib and reduce its concentrations in plasma. The solubility of acalabrutinib is pH-dependent and decreases with increasing pH.', 'DDInter', 1767317474, 'Concomitant use of acalabrutinib with proton pump inhibitors should generally be avoided. If acid suppression therapy is required, H2-receptor antagonists or antacids should be considered. The manufacturer recommends taking acalabrutinib 2 hours before H2-receptor antagonists and separating the dosing of antacids by at least 2 hours.', 'Absorption', 'Concomitant use of acalabrutinib with proton pump inhibitors should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1326653, 'Acalabrutinib', 'Ranolazine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of acalabrutinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317474, 'Caution is advised when acalabrutinib is used with CYP450 3A4 inhibitors. Patients should be monitored for increased adverse effects such as nausea, vomiting, diarrhea, hemorrhage, infection, cytopenias, and atrial fibrillation or flutter, and the acalabrutinib dosage adjusted as necessary.', 'Metabolism', 'Caution is advised when acalabrutinib is used with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1326654, 'Acalabrutinib', 'Regorafenib', 'Major', 'Coadministration of regorafenib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Treatment with regorafenib is associated with a high frequency of hemorrhage and thrombocytopenia. Coadministration of regorafenib with strong UGT1A9 inhibitors such as mefenamic acid or diflunisal may theoretically increase systemic exposure to regorafenib.', 'DDInter', 1767317474, 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with regorafenib. Close clinical and laboratory observation for bleeding complications is recommended during therapy. The INR should be monitored more frequently during coadministration of warfarin.', 'Synergism', 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with regorafenib.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1326655, 'Acalabrutinib', 'Reteplase', 'Major', 'Coadministration of acalabrutinib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications.', 'DDInter', 1767317474, 'Concomitant use of acalabrutinib in patients receiving other medications that interfere with platelet function or coagulation should be approached with caution. Close clinical and laboratory monitoring for bleeding complications is recommended during therapy. Patients should be advised to promptly report any signs and symptoms of bleeding to their physician.', 'Synergism', 'Concomitant use of acalabrutinib in patients receiving other medications that interfere with platelet function or coagulation should be approached with caution.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1326656, 'Acalabrutinib', 'Ribociclib', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of acalabrutinib, which is primarily metabolized by the isoenzyme. Increased acalabrutinib exposure may potentiate the risk of toxicities such as hemorrhage, infection, cytopenias, malignancies, and atrial fibrillation or flutter.', 'DDInter', 1767317474, 'The dosage of acalabrutinib should be reduced to 100 mg once daily when coadministered with moderate CYP450 3A4 inhibitors.', 'Metabolism', 'The dosage of acalabrutinib should be reduced to 100 mg once daily when coadministered with moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1326657, 'Acalabrutinib', 'Rifabutin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of acalabrutinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317474, 'The potential for diminished pharmacologic effects of acalabrutinib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of acalabrutinib should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
