INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1326686, 'Acalabrutinib', 'St. John''s Wort', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of acalabrutinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317474, 'Concomitant use of acalabrutinib with potent CYP450 3A4 inducers should generally be avoided. If coadministration cannot be avoided, the manufacturer recommends increasing the dosage of acalabrutinib to 200 mg twice daily.', 'Metabolism', 'Concomitant use of acalabrutinib with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1326687, 'Acalabrutinib', 'Stiripentol', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of acalabrutinib, which is primarily metabolized by the isoenzyme. Increased acalabrutinib exposure may potentiate the risk of toxicities such as hemorrhage, infection, cytopenias, malignancies, and atrial fibrillation or flutter.', 'DDInter', 1767317474, 'The dosage of acalabrutinib should be reduced to 100 mg once daily when coadministered with moderate CYP450 3A4 inhibitors.', 'Metabolism', 'The dosage of acalabrutinib should be reduced to 100 mg once daily when coadministered with moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1326688, 'Acalabrutinib', 'Streptokinase', 'Major', 'Coadministration of acalabrutinib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications.', 'DDInter', 1767317474, 'Concomitant use of acalabrutinib in patients receiving other medications that interfere with platelet function or coagulation should be approached with caution. Close clinical and laboratory monitoring for bleeding complications is recommended during therapy. Patients should be advised to promptly report any signs and symptoms of bleeding to their physician.', 'Synergism', 'Concomitant use of acalabrutinib in patients receiving other medications that interfere with platelet function or coagulation should be approached with caution.', NULL, NULL, 'Major', 'pharmacodynamic');
