INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (9667, 'Iobenguane (I-131)', 'Methylphenidate', 'Major', 'Coadministration with drugs that reduce catecholamine uptake or deplete catecholamine stores may interfere with iobenguane I-131 uptake into neuroendocrine tumors such as pheochromocytoma and paraganglioma that express high levels of norepinephrine transporter on their cell surfaces. Since iobenguane is similar in structure to norepinephrine and is subject to the same uptake and accumulation pathways as norepinephrine, drugs that alter norepinephrine disposition in adrenergic nerve terminals and presynaptic storage vesicles will likewise affect iobenguane.', NULL, 'Drugs that reduce catecholamine uptake or deplete catecholamine stores should be discontinued for at least five biological half-lives before administration of either the dosimetry dose or a therapeutic dose of iobenguane I-131.', NULL, 'Drugs that reduce catecholamine uptake or deplete catecholamine stores should be discontinued for at least five biological half-lives before administration of either the dosimetry dose or a therapeutic dose of iobenguane I-131. Do not administer these drugs until at least 7 days after each iobenguane I-131 dose. Patients should be monitored for the occurrence of clinically significant withdrawal symptoms, especially patients with elevated levels of circulating catecholamines and their metabolites.', 'Distribution', 'Pemoline, Atomoxetine, Solriamfetol, Caffeine, Dexmethylphenidate, Armodafinil, Citicoline, Modafinil', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/9671/', '[1] "Product Information. Azedra (iobenguane I-131)." Progenics Pharmaceuticals, Inc., New York, NY.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (9668, 'Ioflupane I-123', 'Methylphenidate', 'Moderate', 'Drugs that bind to the dopamine transporter with high affinity may interfere with the image obtained using ioflupane I-123, which also binds to the dopamine transporter. These drugs include amoxapine, amphetamine, benztropine, bupropion, buspirone, cocaine, diethylpropion, ketamine, mazindol, methamphetamine, methylphenidate, modafinil, pemoline, phencyclidine (PCP), phentermine, phenylpropanolamine, pipradrol, selegiline, sertraline, and trihexyphenidyl. Selective serotonin reuptake inhibitors (SSRIs) and serotonin-norepinephrine reuptake inhibitors (SNRIs) may increase or decrease ioflupane binding to the dopamine transporter. Whether discontinuation of these drugs prior to ioflupane I-123 administration may help to minimize potential interference is unknown.', NULL, 'Clinicians using ioflupane I-123 should be aware of possible diagnostic interference by drugs that can compete for binding to the dopamine transporter.', NULL, 'Clinicians using ioflupane I-123 should be aware of possible diagnostic interference by drugs that can compete for binding to the dopamine transporter.', 'Others', 'Atomoxetine, Solriamfetol, Caffeine, Citicoline', 'Indium In-111 pentetate', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/9672/', '[1] "Product Information. DaTscan (ioflupane I-123)." GE Healthcare, Princeton, NJ.[2] "Product Information. Viibryd (vilazodone)." Trovis Pharmaceuticals LLC, New Haven, CT.', 1767369485);
