INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (9727, 'Trimipramine', 'Methylphenidate', 'Moderate', 'The coadministration with methylphenidate may increase the serum concentrations of tricyclic antidepressants (TCAs). Case reports involving primarily methylphenidate and imipramine have suggested favorable as well as unfavorable clinical effects from this combination. In vitro studies suggest that methylphenidate may inhibit the metabolism of imipramine and other TCAs, although the extent is probably subject to considerable interindividual variation.', NULL, 'Pharmacologic response to TCAs should be monitored more closely whenever methylphenidate (racemic) or dexmethylphenidate (the more pharmacologically active d-enantiomer) is added to or withdrawn from therapy, and the TCA dosage adjusted as necessary.', NULL, 'Pharmacologic response to TCAs should be monitored more closely whenever methylphenidate (racemic) or dexmethylphenidate (the more pharmacologically active d-enantiomer) is added to or withdrawn from therapy, and the TCA dosage adjusted as necessary.', 'Metabolism', 'Pemoline, Metamfetamine, Solriamfetol, Caffeine, Citicoline', 'Maprotiline, St. John''s Wort, Oxitriptan, Viloxazine, Milnacipran, Tryptophan, Levomilnacipran, Vilazodone', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/9731/', '[1] Gwirtsman HE, Szuba MP, Toren L, Feist M "The antidepressant response to tricyclics in major depressives is accelerated with adjunctive use of methylphenidate." Psychopharmacol Bull 30 (1994): 157-64[2] Meyers B "Treatment of imipramine-resistant depression and lithium-refractory mania through drug interactions." Am J Psychiatry 135 (1978): 1420-1[3] Whart