INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (9966, 'Epinephrine', 'Carteolol', 'Major', 'Noncardioselective beta-blockers can significantly enhance the pressor response to epinephrine. The mechanism involves blockade of beta-2 adrenergic receptors in the peripheral vasculature, resulting in unopposed alpha-adrenergic effect of epinephrine that is responsible for vasoconstriction. Beta-blockers may attenuate the response to epinephrine in the treatment of anaphylactic reactions. Noncardioselective beta-blockers, in particular, can antagonize the bronchodilating effects of epinephrine by blocking beta-2 adrenergic receptors in smooth muscles of the bronchial tree.', NULL, 'Extreme caution and close monitoring of cardiovascular status are indicated when epinephrine is administered to patients treated with noncardioselective beta-blockers.', NULL, 'Extreme caution and close monitoring of cardiovascular status are indicated when epinephrine is administered to patients treated with noncardioselective beta-blockers. A dosage reduction of epinephrine may be necessary. Withdrawal of beta-blockers before anesthesia may increase the risk of myocardial ischemia and is not recommended. The interaction is not expected to occur with local anesthetics used in dental surgery that contain very low concentrations of epinephrine.', 'Synergism', 'Latanoprost, Apraclonidine, Methazolamide, Neostigmine, Physostigmine, Acetylcholine, Diclofenamide, Bimatoprost, Travoprost, Acetazolamide, Brinzolamide, More', 'Tetracycline, Minocycline, Amphotericin B, Miconazole, Doxycycline, Metronidazole, Clotrimazole, Chlorhexidine, Neomycin, Mometasone furoate, Umeclidinium, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https:/