INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (9969, 'Epinephrine', 'Chlorpromazine', 'Moderate', 'Phenothiazines and other neuroleptics may inhibit or reverse the pressor effect of adrenaline (epinephrine), dopamine, and similar vasoconstrictors. Many of these agents, including the atypical antipsychotics, exhibit alpha-1 adrenergic blocking activity and produce hypotension as an adverse effect. Use of adrenaline or dopamine for drug-induced hypotension and circulatory collapse in patients receiving neuroleptic therapy may cause a paradoxical further lowering of blood pressure, since beta stimulation may worsen hypotension in the setting of alpha blockade.', NULL, 'Adrenaline, dopamine, and similar vasoconstrictors should not be used to treat drug-induced hypotension and circulatory collapse in patients taking phenothiazines or other neuroleptic agents.', NULL, 'Adrenaline, dopamine, and similar vasoconstrictors should not be used to treat drug-induced hypotension and circulatory collapse in patients taking phenothiazines or other neuroleptic agents. Alternative vasopressor agents such as metaraminol, noradrenaline (norepinephrine), or phenylephrine should be considered, and vital signs closely monitored.', 'Antagonism', 'Cariprazine, Pimavanserin', 'Tetracycline, Minocycline, Miconazole, Doxycycline, Clotrimazole, Triamcinolone, Chlorhexidine, Neomycin, Mometasone, Mometasone furoate, Beclomethasone dipropionate, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/9973/', '[1] "Product Information. Seroquel (quetiapine)." Zeneca Pharmaceuticals, Wilmington, DE.[2] "Product Information. Clozaril (clozapine)." Novartis Pharmaceuticals, East Hanover, NJ.[