INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1326705, 'Acalabrutinib', 'Tipranavir', 'Major', 'Theoretically, tipranavir may potentiate the risk of bleeding in patients treated with agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, high supplemental dosages of vitamin E, or agents that commonly cause thrombocytopenia.', 'DDInter', 1767317474, 'Caution is advised if tipranavir/ritonavir is used in combination with other drugs that affect hemostasis. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if tipranavir/ritonavir is used in combination with other drugs that affect hemostasis.', NULL, NULL, 'Major', 'pharmacodynamic');
