INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (5354, 'Codeine', 'Risperidone', 'Major', 'Concomitant use of opioids with benzodiazepines or other central nervous system (CNS) depressants (e.g., nonbenzodiazepine sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may result in profound sedation, respiratory depression, coma, and death. The risk of hypotension may also be increased with some CNS depressants (e.g., alcohol, benzodiazepines, phenothiazines).', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, with cautious titration and dosage adjustments when needed. Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/5358/', '[1] US Food and Drug Administration "FDA warns about serious risks and death when combining opioid pain or cough medicines with benzodiazepines; requires its strongest warning. Available from: URL: http://www.fda.gov/downloads/Drugs/DrugSafety/UCM518672.pdf." ([2016, Aug 31]):', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (5355, 'Codeine', 'Ritonavir', 'Moderate', 'Drugs that are inhibitors of CYP450 2D6 may interfere with the analgesic effect of codeine. The mechanism is decreased in vivo conversion of codeine to morphine, a metabolic reaction mediated by CYP450 2D6.', NULL, 'The possibility of reduced or inadequate pain relief should be considered in patients receiving codeine with drugs that inhibit CYP450 2D6.', NULL, 'The possibility of reduced or inadequate pain relief should be considered in patients receiving codeine with drugs that inhibit CYP450 2D6. An increase in the codeine dosage or a different analgesic agent may be necessary in patients requiring therapy with CYP450 2D6 inhibitors.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/5359/', '[1] Sindrup SH, Brosen K, Bjerring P, et al. "Codeine increases pain threshold to copper vapor laser stimuli in extensive but not poor metabolizers of sparteine." Clin Pharmacol Ther 49 (1991): 686-93[2] Caraco Y, Sheller J, Wood JJ "Pharmacogenetic determination of the effects of codeine and prediction of drug interactions." J Pharmacol Exp Ther 278 (1996): 1165-74[3] Sindrup SH, Arendt-Nielsen L, Brosen K, et al "The effect of quinidine on the analgesic effect of codeine." Eur J Clin Pharmacol 42 (1992): 587-92[4] Zhou SF "Polymorphism of human cytochrome P450 2D6 and its clinical significance: part II." Clin Pharmacokinet 48 (2009): 761-804[5] Desmeules J, Dayer P, Gascon MP, Magistris M "Impact of genetic and environmental factors on codeine analgesia." Clin Pharmacol Ther 45 (1989): 122[6] Thorn CF, Klein TE, Altman RB "Codeine and morphine pathway.