INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (5366, 'Codeine', 'Sildenafil', 'Moderate', 'Acute intake of opiates during sildenafil use may result in prolonged erections. The proposed mechanism is an opiate-induced acute increase in cyclic guanosine monophosphate concentrations in peripheral nerve endings, which can be additive with that produced by sildenafil.', NULL, 'Patients using sildenafil should be advised of the potential for prolonged erections during acute intake of opiates, and to contact their physician if problems occur.', NULL, 'Patients using sildenafil should be advised of the potential for prolonged erections during acute intake of opiates, and to contact their physician if problems occur. The interaction may not occur during chronic use of opiates.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/5370/', '[1] Goldmeier D, Lamba H "Prolonged erections produced by dihydrocodeine and sildenafil." BMJ 324 (2002): 1555[2] Jetter A, Kinzig-Schippers M, Walchner-Bonjean M, et al. "Effects of grapefruit juice on the pharmacokinetics of sildenafil." Clin Pharmacol Ther 71 (2002): 21-29', 1767363020);
