INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (5436, 'Dichloralphenazone', 'Aldesleukin', 'Moderate', 'Sedatives, tranquilizers, muscle relaxants, antidepressants, and other central nervous system (CNS) depressants may have additive CNS-depressant effects with aldesleukin. Aldesleukin has been associated with CNS effects including depression, confusion, and drowsiness, which may progress to coma.', NULL, 'Caution is advised if aldesleukin is used with sedatives, hypnotics, tranquilizers, or other CNS-active medications.', NULL, 'Caution is advised if aldesleukin is used with sedatives, hypnotics, tranquilizers, or other CNS-active medications. Patients should be monitored for CNS depression and other CNS adverse effects. Treatment with aldesleukin should be discontinued if severe lethargy or somnolence develop.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/5440/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Agencia Espaola de Medicamentos y Productos Sanitarios Healthcare "Centro de informacin online de medicamentos de la AEMPS - CIMA. Available from: URL: https://cima.aemps.es/cima/publico/home.html." ([2018]):[3] "Product Information. Proleukin (aldesleukin)." Chiron Therapeutics, Emeryville, CA.[4] "Product Information. Zohydro ER (HYDROcodone)." Zogenix, Inc, San Diego, CA.', 1767363020);
