INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1327053, 'Esmolol', 'Lidocaine', 'Moderate', 'Some beta-blockers may increase lidocaine levels and risk of toxicity. The proposed mechanism is enzyme inhibition and/or decreased cardiac output and hepatic blood flow resulting in decreased hepatic metabolism of lidocaine. In addition beta-blockers and lidocaine may also have additive negative inotropic effects on the heart.', 'DDInter', 1767317474, 'Patients receiving concurrent therapy should be monitored for drowsiness, mental status changes, bradycardia, and hypotension. Lidocaine levels should be obtained when clinically necessary. If toxicity is suspected, the lidocaine infusion should be decreased, as possible.', 'Metabolism, Synergism', 'Patients receiving concurrent therapy should be monitored for drowsiness, mental status changes, bradycardia, and hypotension.', NULL, NULL, 'Moderate', 'pharmacodynamic');
