INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1327120, 'Nitazoxanide', 'Lidocaine', 'Moderate', 'Coadministration with nitazoxanide may increase the free plasma concentrations of drugs and/or their active metabolites that are highly bound to plasma proteins. The pharmacologic effects of some drugs may be increased by this interaction. The proposed mechanism is competitive displacement from protein binding sites by tizoxanide, the active metabolite of nitazoxanide that is greater than 99.9% bound to plasma proteins.', 'DDInter', 1767317474, 'Caution is advised when other highly protein-bound drugs are used with nitazoxanide, particularly those with a narrow therapeutic index. Patients should be monitored more closely following the addition or withdrawal of nitazoxanide. When performing therapeutic drug monitoring, total plasma concentrations should be interpreted cautiously due to the possibility of increased free (unbound) drug.', 'Distribution', 'Caution is advised when other highly protein-bound drugs are used with nitazoxanide, particularly those with a narrow therapeutic index.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1327121, 'Obeticholic acid', 'Lidocaine', 'Moderate', 'Coadministration with obeticholic acid may increase the plasma concentrations of drugs that are substrates of the CYP450 1A2 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 1A2 activity by obeticholic acid and its glycine and taurine conjugates.', 'DDInter', 1767317474, 'Caution is advised when obeticholic acid is prescribed with drugs that undergo metabolism by CYP450 1A2, particularly those with a narrow therapeutic range such as theophylline or tizanidine. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever obeticholic acid is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when obeticholic acid is prescribed with drugs that undergo metabolism by CYP450 1A2, particularly those with a narrow therapeutic range such as theophylline or tizanidine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1327122, 'Octreotide', 'Lidocaine', 'Moderate', 'Somatostatin analogs (e.g., octreotide, lanreotide) can decrease heart rate and may potentiate the bradycardic effects of drugs like beta-blockers, calcium channel blockers, digitalis glycosides, and certain antiarrhythmic agents (e.g., class I antiarrhythmics, adenosine, amiodarone).', 'DDInter', 1767317474, 'Caution is advised if somatostatin analogs are prescribed with agents that exhibit bradycardic effects, including ophthalmic formulations of beta-blockers. Heart rate and blood pressure should be monitored.', 'Synergism', 'Caution is advised if somatostatin analogs are prescribed with agents that exhibit bradycardic effects, including ophthalmic formulations of beta-blockers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1327123, 'Olaparib', 'Lidocaine', 'Moderate', 'Coadministration with olaparib may decrease the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 1A2 and/or 2B6. The proposed mechanism, based on in vitro data, is increased clearance due to olaparib-mediated induction of CYP450 1A2 and/or 2B6.', 'DDInter', 1767317474, 'Caution is advised if olaparib is used concomitantly with drugs that are substrates of CYP450 1A2 and/or 2B6. Pharmacologic response and serum levels should be monitored more closely whenever olaparib is added to or withdrawn from therapy with these drugs. Dosage adjustments may be required if an interaction is suspected.', 'Metabolism', 'Caution is advised if olaparib is used concomitantly with drugs that are substrates of CYP450 1A2 and/or 2B6.', NULL, NULL, 'Moderate', 'pharmacodynamic');
