INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1327219, 'Chlorpheniramine', 'Apomorphine', 'Moderate', 'Central nervous system (CNS) depressant effects may be additively or synergistically increased in patients using apomorphine in combination with other drugs that can also cause these effects. Apomorphine alone has been frequently associated with somnolence and dizziness. Patients may suddenly fall asleep during activities of daily living.', 'DDInter', 1767317474, 'The use of other sedating drugs should generally be avoided during apomorphine treatment. Patients prescribed these agents concurrently should be monitored for potentially excessive or prolonged CNS depression, especially if they are elderly or debilitated. Ambulatory patients should be made aware of the possibility of additive CNS effects (e.g., drowsiness, dizziness, lightheadedness, confusion) and counseled to avoid activities requiring mental alertness until they know how these agents affect them.', 'Synergism', 'The use of other sedating drugs should generally be avoided during apomorphine treatment.', NULL, NULL, 'Moderate', 'pharmacodynamic');
