INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1327274, 'Citalopram', 'Chlorpheniramine', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317474, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1327275, 'Clemastine', 'Chlorpheniramine', 'Moderate', 'Agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) may have additive effects when used in combination. Excessive parasympatholytic effects may result in paralytic ileus, hyperthermia, heat stroke, and the anticholinergic intoxication syndrome. Peripheral symptoms of intoxication commonly include mydriasis, blurred vision, flushed face, fever, dry skin and mucous membranes, tachycardia, urinary retention, and constipation. Central symptoms may include memory loss, disorientation, incoherence, hallucinations, psychosis, delirium, hyperactivity, twitching or jerking movements, stereotypy, and seizures. Central nervous system-depressant effects may also be additively or synergistically increased when these agents are combined, especially in elderly or debilitated patients.', 'DDInter', 1767317474, 'Caution is advised when agents with anticholinergic properties are combined, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked. Patients should be advised to notify their physician promptly if they experience potential symptoms of anticholinergic intoxication such as abdominal pain, fever, heat intolerance, blurred vision, confusion, and/or hallucinations.', 'Synergism', 'Caution is advised when agents with anticholinergic properties are combined, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1327276, 'Clidinium', 'Phenylephrine', 'Moderate', 'The pressor response to phenylephrine may be potentiated by the vagolytic effect of atropine, which inhibits the reflex bradycardia that would normally accompany any increases in blood pressure induced by phenylephrine. Other antimuscarinic agents may also participate in this interaction, although clinical data are lacking.', 'DDInter', 1767317474, 'Caution is advised if phenylephrine (systemic or ophthalmic) is used in combination with atropine or other antimuscarinic agents. Cardiovascular status, including blood pressure and heart rate, should be closely monitored. When using ophthalmic formulations, measures to minimize systemic absorption should be employed, such as digital compression of the lacrimal sac or lid closure after instillation. A milder strength (< 10%) is preferable if phenylephrine ophthalmic solution is given.', 'Synergism', 'Caution is advised if phenylephrine (systemic or ophthalmic) is used in combination with atropine or other antimuscarinic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1327277, 'Clobazam', 'Chlorpheniramine', 'Moderate', 'Coadministration with clobazam may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 2D6. The mechanism is decreased clearance due to inhibition of CYP450 2D6 activity by clobazam.', 'DDInter', 1767317474, 'Caution is advised if clobazam must be used concomitantly with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever clobazam is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if clobazam must be used concomitantly with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
