INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1327415, 'Chlorpheniramine', 'Lasmiditan', 'Moderate', 'Coadministration with alcohol or other central nervous system (CNS) depressants may enhance the sedative effects of lasmiditan and increase the likelihood and/or severity of cognitive and/or neuropsychiatric adverse reactions. Patients treated with lasmiditan are at risk of CNS depression, including dizziness and sedation, as well as driving impairment.', 'DDInter', 1767317474, 'Caution is advised during coadministration of lasmiditan with alcohol, other CNS depressants, or other agents that cause sedation. Patients should be advised against driving and other activities that require complete mental alertness for at least 8 hours after lasmiditan is administered.', 'Synergism', 'Caution is advised during coadministration of lasmiditan with alcohol, other CNS depressants, or other agents that cause sedation.', NULL, NULL, 'Moderate', 'pharmacodynamic');
