INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1327421, 'Chlorpheniramine', 'Levocetirizine', 'Moderate', 'Concurrent use of cetirizine or levocetirizine with alcohol or other agents that exhibit central nervous system (CNS) depressant effects may result in additive impairment of mental alertness and performance.', 'DDInter', 1767317474, 'Concomitant use of cetirizine or levocetirizine with alcohol or other CNS depressants should generally be avoided if possible. In the event that they are used together, patients should be counseled against driving, operating machinery, or engaging in potentially hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'Concomitant use of cetirizine or levocetirizine with alcohol or other CNS depressants should generally be avoided if possible.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1327422, 'Phenylephrine', 'Levodopa', 'Moderate', 'Coadministration with levodopa may potentiate the cardiovascular effects of sympathomimetic agents (e.g., epinephrine, norepinephrine, isoproterenol, amphetamine). Additive increases in blood pressure and heart rate may occur due to enhanced sympathetic activity. However, data evaluating the interaction are not available.', 'DDInter', 1767317474, 'The use of levodopa with sympathomimetic agents should generally be avoided. If concomitant use is required, close monitoring of the cardiovascular system is recommended and the dose of the sympathomimetic agent may need to be reduced.', 'Synergism', 'The use of levodopa with sympathomimetic agents should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
