INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1327570, 'Scopolamine', 'Phenylephrine', 'Moderate', 'The pressor response to phenylephrine may be potentiated by the vagolytic effect of atropine, which inhibits the reflex bradycardia that would normally accompany any increases in blood pressure induced by phenylephrine. Other antimuscarinic agents may also participate in this interaction, although clinical data are lacking.', 'DDInter', 1767317474, 'Caution is advised if phenylephrine (systemic or ophthalmic) is used in combination with atropine or other antimuscarinic agents. Cardiovascular status, including blood pressure and heart rate, should be closely monitored. When using ophthalmic formulations, measures to minimize systemic absorption should be employed, such as digital compression of the lacrimal sac or lid closure after instillation. A milder strength (< 10%) is preferable if phenylephrine ophthalmic solution is given.', 'Synergism', 'Caution is advised if phenylephrine (systemic or ophthalmic) is used in combination with atropine or other antimuscarinic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
