INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6095, 'Tramadol', 'Rolapitant', 'Moderate', 'Coadministration with rolapitant may increase the plasma concentrations of drugs that are substrates of the CYP450 2D6 isoenzyme. Rolapitant is a moderate CYP450 2D6 inhibitor, with inhibitory effect lasting at least 7 days after a single dose.', NULL, 'Caution is advised when rolapitant is prescribed with drugs that are significantly metabolized by CYP450 2D6, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when rolapitant is prescribed with drugs that are significantly metabolized by CYP450 2D6, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever rolapitant is added to or withdrawn from therapy.', 'Metabolism', 'Naltrexone, Oxymorphone, Oxycodone, Methadone, Pentazocine, Buprenorphine, Butorphanol, Fentanyl, Ibuprofen, Nalbuphine, Acetaminophen, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6099/', '[1] "Product Information. Varubi (rolapitant)." Tesaro Inc., Waltham, MA.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
