INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6198, 'Acetazolamide', 'Apomorphine', 'Moderate', 'Apomorphine may potentiate the hypotensive effect of vasodilators and antihypertensive agents. Apomorphine alone has been associated with orthostatic hypotension, hypotension, syncope, and dose-dependent decreases in systolic blood pressure.', NULL, 'Caution and close monitoring for altered efficacy and safety are recommended if patients receive apomorphine with an antihypertensive agent or vasodilator.', NULL, 'Caution and close monitoring for altered efficacy and safety are recommended if patients receive apomorphine with an antihypertensive agent or vasodilator. Patients should be made aware of the possible side effects (e.g., dizziness, lightheadedness, orthostasis) and be cautioned about driving, operating machinery, or performing other hazardous tasks. They should also be advised to avoid rising abruptly from a sitting or recumbent position and to contact their physician if they experience symptoms of hypotension such as dizziness, lightheadedness, or fainting.', 'Synergism', 'Fesoterodine, Pentosan polysulfate, Calcium chloride, Tolterodine, Phenazopyridine, Trospium, Mirabegron, Alprostadil, Acetohydroxamic acid, Yohimbine, Flavoxate, More', 'Tinidazole, Ascorbic acid, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Amphotericin B, Clindamycin, Ciclopirox, Darunavir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6202/', '[1] "Product Information. Apokyn (apomorphine)." Mylan Pharmaceuticals Inc, Morgantown, WV.[2] Bailey DG, Dresser GK "Natural products and adverse drug interactions." Can Med Assoc J 170 (2004): 1531-2[3] Ho PC, 