INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6280, 'Hydroxychloroquine', 'Acetazolamide', 'Moderate', 'Chloroquine or hydroxychloroquine may lower the convulsive threshold, which may antagonize the action of antiepileptic medications. Seizures have been rarely reported in patients on chloroquine or hydroxychloroquine therapy. The mechanism is unknown.', NULL, 'The potential risks and benefits should be evaluated prior to initiating chloroquine or hydroxychloroquine in patients on anticonvulsant therapy.', NULL, 'The potential risks and benefits should be evaluated prior to initiating chloroquine or hydroxychloroquine in patients on anticonvulsant therapy. In addition, patients with a history of epilepsy should be advised about the risk of chloroquine/hydroxychloroquine-induced seizures. Alternative anti-malarial agents may be required. Local treatment guidelines should be consulted.', 'Synergism', 'Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Simeprevir, Lactic acid, Mitapivat, Methazolamide, Trichlormethiazide, Tioconazole, Brinzolamide, Furazolidone, More', 'Artesunate, Lumefantrine, Artemether, Pyrimethamine, Tafenoquine, Atovaquone, Halofantrine, Quinine, Primaquine, Proguanil, Sulfadoxine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6284/', '[1] "Product Information. Plaquenil (R). (hydroxychloroquine)." Sanofi Winthrop Pharmaceuticals, New York, NY.[2] "Product Information. Hydroxychloroquine Sulfate (hydroxychloroquine)." Prasco Laboratories, Cincinnati, OH.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] "Product Information. Aralen (chloroquine)." Sanofi