INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6358, 'Prednisolone', 'Acetazolamide', 'Moderate', 'The concomitant use of corticosteroids and agents that deplete potassium (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may result in increased risk of hypokalemia. Corticosteroids can produce hypokalemia and other electrolyte disturbances via mineralocorticoid effects, the degree of which varies with the agent and route of administration (i.e. systemic vs. local).', NULL, 'Patients receiving potassium-depleting agents with corticosteroids should be monitored closely for development of hypokalemia, particularly if fludrocortisone or large doses of another corticosteroid or adrenocorticotropic agent is given.', NULL, 'Patients receiving potassium-depleting agents with corticosteroids should be monitored closely for development of hypokalemia, particularly if fludrocortisone or large doses of another corticosteroid or adrenocorticotropic agent is given. Potassium supplementation may be necessary. Patients should be advised to notify their physician if they experience signs of electrolyte disturbances such as weakness, lethargy, and muscle pains or cramps.', 'Synergism', 'Tinidazole, Ascorbic acid, Simeprevir, Sulfadiazine, Lactic acid, Mitapivat, Ciclopirox, Vemurafenib, Tioconazole, Furazolidone, Diiodohydroxyquinoline, More', 'Mometasone furoate, Amcinonide, Fluocinolone acetonide, Hydrocortisone butyrate, Fluticasone, Fluocinonide, Fluorometholone, Clobetasol, Fluorometholone, Indomethacin, Fluocinolone acetonide, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6362/', '[1] Morris GC, Egan JG, Jones MK "Hypokalaemic paralysi