INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6395, 'Triamcinolone', 'Acetazolamide', 'Moderate', 'The concomitant use of corticosteroids and agents that deplete potassium (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may result in increased risk of hypokalemia. Corticosteroids can produce hypokalemia and other electrolyte disturbances via mineralocorticoid effects, the degree of which varies with the agent and route of administration (i.e. systemic vs. local).', NULL, 'Patients receiving potassium-depleting agents with corticosteroids should be monitored closely for development of hypokalemia, particularly if fludrocortisone or large doses of another corticosteroid or adrenocorticotropic agent is given.', NULL, 'Patients receiving potassium-depleting agents with corticosteroids should be monitored closely for development of hypokalemia, particularly if fludrocortisone or large doses of another corticosteroid or adrenocorticotropic agent is given. Potassium supplementation may be necessary. Patients should be advised to notify their physician if they experience signs of electrolyte disturbances such as weakness, lethargy, and muscle pains or cramps.', 'Synergism', 'Mometasone furoate, Amcinonide, Fluocinolone acetonide, Hydrocortisone butyrate, Fluticasone, Fluocinonide, Fluorometholone, Clobetasol, Ciclesonide, Mupirocin, Mometasone furoate, More', 'Tinidazole, Ascorbic acid, Simeprevir, Lactic acid, Mitapivat, Vemurafenib, Probenecid, Tioconazole, Furazolidone, Diiodohydroxyquinoline, Oxytetracycline, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6399/', '[1] Morris GC, Egan JG, Jones MK "Hypokalaemic paralysis induced by bolus prednisolone in Graves'' disease." Aust N Z J Med 22 (1992): 312[2] Thomas TP "The complications of systemic corticosteroid therapy in the elderly." Gerontology 30 (1984): 60-5[3] "Product Information. Hydeltrasol (prednisolone)." Merck & Co, Inc, West Point, PA.[4] Ramsahoye BH, Davies SV, el-Gaylani N, Sandeman D, Scanlon MF "The mineralocorticoid effects of high dose hydrocortisone." BMJ 310 (1995): 656-7[5] Powell JR "Steroid and hypokalemic myopathy after corticosteroids for ulcerative colitis. Systemic and tropical application." Am J Gastroenterol 52 (1969): 425-32[6] Thorn GW "Clinical considerations in the use of corticosteroids." N Engl J Med 274 (1966): 775-81[7] Seale JP, Compton MR "Side-effects of corticosteroid agents." Med J Aust 144 (1986): 139-42[8] Chrousos GA, Kattah JC, Beck RW, Cleary PA "Side effects of glucocorticoid treatment. Experience of the Optic Neuritis Treatment Trial." JAMA 269 (1993): 2110-2', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6396, 'Triazolam', 'Acetazolamide', 'Moderate', 'Benzodiazepines may counteract the beneficial effects of acetazolamide in preventing altitude sickness by inhibiting respiratory responses to hypoxia.', NULL, 'Caution is recommended if a CAI and a benzodiazepine must be used together.', NULL, 'Caution is recommended if a CAI and a benzodiazepine must be used together. Until more information is available, it may be prudent to avoid the concurrent use of triazolam and acetazolamide at extreme altitudes.', 'Antagonism', 'Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Sulfadiazine, Lactic acid, Amphotericin B, Clindamycin, Ciclopirox, Vemurafenib, Tioconazole, More', 'Suvorexant, Methohexital, Methylphenobarbital, Pentobarbital, Zaleplon, Dichloralphenazone, Valerian, Butabarbital, Secobarbital, Chloral hydrate, Paraldehyde, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6400/', '[1] Masuyama S, Hirata K, Saito A ""Ondine''s curse": side effect of acetazolamide?" Am J Med 86 (1989): 637[2] Hukkinen SK, Varhe A, Olkkola KT, Neuvonen PJ "Plasma concentrations of triazolam are increased by concomitant ingestion of grapefruit juice." Clin Pharmacol Ther 58 (1995): 127-31[3] Kupferschmidt HHT, Ha HR, Ziegler WH, Meier PJ, Krahenbuhl S "Interaction between grapefruit juice and midazolam in humans." Clin Pharmacol Ther 58 (1995): 20-8[4] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77[5] "Product Information. Valium (diazepam)." Roche Laboratories, Nut