INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6581, 'Famotidine', 'Enoxacin', 'Minor', 'Depending on the route of administration, enoxacin effects may be altered by certain histamine-2 (H2) receptor antagonists. Oral enoxacin effects may be decreased due to reduced gastrointestinal solubility and absorption as a result of H2 antagonist-induced reductions in gastric pH. Reports of this interaction exist for parenteral ranitidine. On the other hand, pharmacologic effects of parenteral enoxacin may be increased by cimetidine. The mechanism is speculated to be competition for active renal secretion. Reports of enoxacin interactivity with other H2 antagonists do not exist, although theoretically, all H2 antagonists may produce a similar effect on oral enoxacin. Close clinical monitoring for altered enoxacin effect is recommended in patients receiving concomitant enoxacin and an H2 antagonist.', NULL, '-', NULL, '-', 'Absorption, Excretion', 'Trovafloxacin, Delafloxacin, Nalidixic acid, Cinoxacin', 'Tinidazole, Pantoprazole, Levofloxacin, Dexlansoprazole, Rabeprazole, Tetracycline, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Rifabutin, Esomeprazole, More', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6585/', '[1] Misiak PM, Eldon MA, Toothaker RD, Sedman AJ "Effects of oral cimetidine or ranitidine on the pharmacokinetics of intravenous enoxacin." J Clin Pharmacol 33 (1993): 53-6[2] Lebsack ME, Nix D, Ryerson B, et al "Effect of gastric acidity on enoxacin absorption." Clin Pharmacol Ther 52 (1992): 252-6', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6582, 'Famotidine', 'Entecavir', 'Moderate', 'Coadministration of entecavir with another drug that is also eliminated by active tubular secretion may result in increased plasma concentrations of one or both drugs due to competitive inhibition of transporters in the renal tubules. Drugs (and/or their metabolites) that are thought to undergo active tubular secretion include acyclovir, allopurinol, aminosalicylic acid, cidofovir, cimetidine, creatine, dyphylline, famciclovir, famotidine, flecainide, ganciclovir, levetiracetam, metformin, methotrexate, midodrine, mycophenolic acid, oseltamivir, pralatrexate, probenecid, procainamide, quinidine, ranitidine, tenofovir, triamterene, trimethoprim, valacyclovir, valganciclovir, zalcitabine, zidovudine, and many of the beta-lactam and quinolone antibiotics.', NULL, 'Patients receiving entecavir with another drug that undergoes active tubular secretion should be monitored for excessive pharmacologic effects of both drugs, and the dosages adjusted as necessary.', NULL, 'Patients receiving entecavir with another drug that undergoes active tubular secretion should be monitored for excessive pharmacologic effects of both drugs, and the dosages adjusted as necessary.', 'Excretion', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, Etravirine, More', 'Tinidazole, Pantoprazole, Tetracycline, Dexlansoprazole, Rabeprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Rifabutin, Nizatidine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6586/', '[1] "Product Information. Baraclude (entecavir)." Bristol-Myers Squibb, Princeton, NJ.[2] "Product Information. Baraclude (entecavir)." Bristol-Myers Squibb, Princeton, NJ.', 1767369485);
